INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (69, 'no bake  cookie crumble cheesecake', '71635', 135, '["weeknight", "time-to-make", "course", "preparation", "desserts", "cheesecake", "4-hours-or-less"]', ARRAY[387.6, 39.0, 98.0, 12.0, 12.0, 67.0, 11.0], 8, ARRAY['in blender , sprinkle gelatin over cold milk', 'let stand 2 minutes', 'add hot milk and process at low until dissolved , about 2 minutes', 'add cream cheese , sugar and vanilla and process until blended', 'arrange mini chocolate chips in the bottom of the crust', 'pour in gelatin mixture', 'sprinkle with your favourite crushed cookies', 'chill until firm , about 2 hours'], 'yet another one i have not made, but have had for awhile. sounds good and very versatile as you choose what kind of cookies to use! also sounds extremely easy(not to mention quick) as you whip this all up in a blender and put into a storebought crust!! most of my cheesecake making has been baked ones so i hope this ', ARRAY['gelatin', 'milk', 'cream cheese', 'sugar', 'vanilla extract', 'miniature semisweet chocolate chips', 'prepared graham cracker crusts', 'cookie'], 8, '/static/69.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (70, 'now and later  vegetarian empanadas', '38276', 90, '["weeknight", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "lunch", "main-dish", "vegetables", "mexican", "vegetarian", "dietary", "gifts", "one-dish-meal", "oamc-freezer-make-ahead", "number-of-servings", "4-hours-or-less"]', ARRAY[477.1, 36.0, 24.0, 21.0, 26.0, 61.0, 18.0], 41, ARRAY['in a pot , cover carrots with water to cover', 'bring to a boil , then lower heat', 'simmer 10 minutes or until just tender', 'while simmering carrots , pour contents of can of tomatoes into a bowl and squish into little pieces with your hands', 'wash hands', 'blend chili powder , cumin , oregano , and cayenne into tomatoes', 'take the 2 tbs of flour and blend with 1 / 4 cup cold water in a small cup', 'pour this into tomato mixture and stir to blend', 'drain carrots , and cut into 3 / 4 in pieces', 'put both aside for now', 'melt butter on medium heat in a large fring pan', 'add onion , mushrooms , zucchini , celery , green pepper , and garlic and saute', 'stir continuously until onion is limp', 'stir tomato mixture into frying pan and bring to a simmer', 'cover and simmer for 2 or 3 minutes , then uncover and cook for about 5 minutes over med-high heat until liquid has evaporated', 'stir in carrots and cheese', 'salt to taste', 'when cheese has melted put pan in refrigerator to quickly cool vegetables', 'stir now and then to help cool faster', 'now , begin empanada crust', 'resift flour with baking powder in a bowl', 'quickly cut butter and shortening into bowl until it looks like a coarse meal', 'make a well in the center', 'add soy sauce and cold water together', 'stir vigorously with a fork until dough follows the fork around bowl', 'personal observation: the dough seems rather"frothy" at this point', 'turn dough onto a lightly floured surface and knead gently for about 10 folds to remove stickyness', 'divide dough into 8 equal pieces', 'make into a ball and roll into an 8 inch circle', 'spoon about 3 / 4 cup vegetable filling onto center of circle', 'moisten edges of circle with water and bring together at top , pinching and fluting edges', 'mine looked like balls of dough and turned out fine', 'lift empanada onto ungreased cookie sheet', 'repeat until done', 'if you fill more than one cookie sheet put the full one in the refrigerator', 'when done , prick the top of each empanada with a fork and brush with egg and water mixture', 'if you are going to cook some of them right away , pre-heat oven to 400 and cook 25 to 30 minutes on an ungreased cookie sheet', 'take the ones for later and freeze uncovered on cookie sheet overnight', 'when frozen solid wrap in foil or put in freezer bags', 'label with instructions: bake uncovered in pre-heated 425 degree oven for 30 minutes', 'let cool some before serving'], 'this is from ', ARRAY['carrots', 'butter', 'onion', 'sliced mushrooms', 'zucchini', 'celery', 'green pepper', 'cloves', 'tomatoes', 'chili powder', 'ground cumin', 'oregano', 'cayenne pepper', 'flour', 'water', 'monterey jack cheese', 'salt', 'all-purpose flour', 'baking powder', 'vegetable shortening', 'soy sauce', 'egg'], 22, '/static/70.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (72, 'one bowl  perfect pound cake', '26835', 70, '["weeknight", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "desserts", "oven", "easy", "potluck", "dinner-party", "heirloom-historical", "holiday-event", "kid-friendly", "picnic", "vegetarian", "cakes", "grains", "dietary", "comfort-food", "wedding", "pasta-rice-and-grains", "taste-mood", "to-go", "equipment", "4-hours-or-less"]', ARRAY[456.6, 32.0, 164.0, 15.0, 11.0, 63.0, 20.0], 7, ARRAY['preheat oven to 325 degrees and grease and flour a tube or bundt pan', 'combine all ingredients in a large bowl and blend at low speed', 'beat 3 minutes at medium speed', 'pour into prepared pan', 'bake for 1 hour or until top springs back when touched lightly in center', 'cool in pan for about 15 minutes then remove and cool completely on rack', 'serve plain or glaze if you like'], 'this is the perfect pound cake recipe...plus so easy to prepare and only one bowl to wash! this is a firm cake, so if you are into cutting and shaping cakes, this works well. i have used it for christening and communion cakes, creating crosses, etc and used buttercream frosting. i have also made it in a 9 x 13 cake pan...just adjust time accordingly. the addition of the yogurt really makes a difference in the taste.', ARRAY['all-purpose flour', 'sugar', 'salt', 'baking soda', 'lemon, rind of', 'vanilla', 'butter', 'yogurt', 'eggs'], 9, '/static/72.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (73, 'one pot  brownies', '32271', 40, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "desserts", "easy", "cookies-and-brownies", "chocolate", "bar-cookies", "brownies", "dietary"]', ARRAY[1389.5, 129.0, 410.0, 26.0, 42.0, 211.0, 51.0], 8, ARRAY['melt chocolate and butter in heavy pot', 'let cool slightly , then add sugar and mix well', 'add eggs , mix well , then beat in flour', 'add vanilla', 'add nuts or raisins , if desired', 'stir well', 'bake in 9 1 / 2 x 13 inch greased pan for 20-25 minutes at 375 degrees', 'do not overbake'], 'this recipe was submitted by mrs. stuart padnos for a cookbook fund raiser put out by members of the junior welfare league, holland, michigan about 30 or so years ago. my mom brought the cookbook back to me as a souvenir of their trip. i have made these brownies all these years and have great luck with them every time. i like to top them with an easy cooked chocolate fudge icing that i got from a bowling partner several years ago. these brownies are like a chocolate cake-not gooey like some recipes and i do personally like them plain without nuts or raisins.', ARRAY['unsweetened chocolate squares', 'butter', 'sugar', 'eggs', 'flour', 'vanilla', 'nuts'], 7, '/static/73.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (80, 'real  italian bolognese sauce', '83950', 160, '["time-to-make", "course", "cuisine", "preparation", "sauces", "condiments-etc", "european", "italian", "dietary", "4-hours-or-less"]', ARRAY[1260.7, 97.0, 11.0, 71.0, 103.0, 119.0, 38.0], 16, ARRAY['in a dutch oven or medium size pot , heat butter and olive oil over medium heat until butter begins to froth', 'add onion , celery , carrot , garlic , and bacon', 'cook until onions are translucent', 'remove bacon and remove fat', 'chop lean portions of bacon in small pieces and return to pot', 'add ground beef and ground pork , and cook until meat loses red , raw color', 'raise heat and add wine and consomme', 'cook sauce until wine and consomme are mostly evaporated', 'turn heat down to simmer and add oregano , salt , pepper , sage , red pepper flakes , and nutmeg', 'let cook for approximately 20 minutes', 'add crushed tomatoes and bring heat to a boil', 'once the mixture comes to a boil , return to simmer', 'let sauce simmer partially covered for about 2 to 4 hours , stirring occasionally to prevent sticking', 'about 5 to 10 minutes before serving , add milk', 'sauce can now be added to cooked penne pasta , spaghetti or many other pastas to your liking', 'remaining sauce may be frozen for up to two months for future use'], 'after traveling throughout italy, savoring the fine tastes of bolognese from the many different regions, i decided to formulate my own. try it, you will love it.', ARRAY['bacon', 'onion', 'celery', 'carrot', 'garlic', 'butter', 'olive oil', 'lean ground beef', 'ground pork', 'beef consomme', 'dry white wine', 'crushed tomatoes', 'salt', 'black pepper', 'rubbed sage', 'oregano', 'red pepper flakes', 'nutmeg', 'milk', 'penne pasta'], 20, '/static/80.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (84, 'rise and shine  german fruit pancake', '71457', 45, '["60-minutes-or-less", "time-to-make", "course", "cuisine", "preparation", "north-american", "pancakes-and-waffles", "breakfast", "american", "dietary", "midwestern"]', ARRAY[253.0, 13.0, 64.0, 10.0, 11.0, 16.0, 13.0], 11, ARRAY['preheat oven to 450 degrees', 'with an electric mixer or wire whisk , beat eggs until frothy', 'whisk in flour', 'stir in milk and salt', 'melt butter in a 10-inch oven-proof skillet', 'pour mixture into hot skillet and place in preheated oven', 'bake 15 minutes or until pancake is puffed and golden', 'the center will be shaped like a bowl', 'remove from oven', 'fill with fruit , dust with powdered sugar and sprinkle with almonds', 'serve immediately'], 'source: kbyus culinary creations with mary crafts', ARRAY['eggs', 'flour', 'milk', 'salt', 'butter', 'powdered sugar', 'apple pie filling', 'toasted almonds'], 8, '/static/84.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (86, 'smells like sunday  chicken fricassee with meatballs', '103948', 90, '["time-to-make", "course", "main-ingredient", "preparation", "main-dish", "beef", "poultry", "chicken", "dietary", "low-sodium", "low-carb", "ground-beef", "low-in-something", "meat", "chicken-thighs-legs", "4-hours-or-less"]', ARRAY[692.2, 53.0, 16.0, 17.0, 102.0, 36.0, 12.0], 20, ARRAY['first of all make the meatballs by combining all the meatball ingredients in a bowl , mixing well', 'shape mixture into 1 1 / 2 inch balls and brown in a single layer in the oil , over medium high heat , turning occasionally , until well browned on all sides , about 7 to 8 minutes', 'alternatively meatballs can be browned in a hot oven', 'remove from the skillet and drain on paper towels', 'wipe the skillet clean', 'wash chicken and pat dry', 'in a shallow bowl , combine the 3 / 4 cup flour , seasonings and mix well', 'dredge the chicken in this mixture to coat , shaking off any excess flour', 'in skillet , heat oil over medium high heat until hot but not smoking', 'add the chicken and brown on all sides about 4 minutes per side', 'remove and set aside', 'pour off the oil from the skillet , leaving only a thin layer', 'add the chopped onion and saute over medium high heat until tender and lightly browned , about 8 minutes', 'stir in the 2 tsp flour and mix well', 'add the stock and wine , stirring gently to blend', 'cook until thickened', 'return the chicken and meatballs to skillet', 'cover and braise over moderate heat , stirring occasionally , for 45 minutes until chicken and meatballs are cooked through', 'adjust seasoning and serve with rice or little noodles', 'alternatively , chicken , meatballs and sauce can be layered in a covered casserole and cooked in a slow oven for several hours'], 'like many family recipes, this one was created out of the need to stretch ingredients. one day when there was just a little chicken, and even less ground beef in the house, an old aunt of mine decreed that the two should be cooked together to make a meal large enough for the whole family. the result was a wonderfully tasty dish that smelled just like sunday dinner, but it was not, because on sundays we always made sure to have enough of either chicken or meat. this is one of my absolute favorite ways to cook chicken. i hope that you enjoy it as well.', ARRAY['boneless skinless chicken thighs', 'all-purpose flour', 'salt', 'fresh ground black pepper', 'dried oregano', 'dried thyme', 'garlic powder', 'vegetable oil', 'onion', 'chicken stock', 'dry white wine', 'breadcrumbs', 'milk', 'lean ground beef', 'poultry seasoning'], 15, '/static/86.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (88, 'some like it hot', '64045', 70, '["weeknight", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "for-1-or-2", "main-dish", "beans", "vegetables", "asian", "dinner-party", "vegetarian", "stove-top", "indonesian", "dietary", "spicy", "soy-tofu", "tempeh", "taste-mood", "equipment", "number-of-servings", "presentation", "served-hot", "4-hours-or-less"]', ARRAY[649.6, 55.0, 104.0, 116.0, 72.0, 39.0, 19.0], 11, ARRAY['start with the topping , in a frying pan , dry fry the coconut and peanuts until golden brown , tossing it so it wont burn , remove from the pan and let cool', 'mix together the soy sauce , cayenne , sambal , ginger , sugar , garlic and water , set aside , you will use this after you fried the tempeh', 'in a wok or frying pan , heat the tablespoon of oil , fry the onion and garlic for one minute , add the coriander , cumin and if you use them the chili peppers , fry this for two minutes', 'add the carrots , beans and tomatoes , fry until you see the tomatoes going soft , add the stock , bring to the boil and simmer for about 10 minutes', 'heat the oil in a frying pan large enough to hold the tempeh strips in one layer', 'fry the tempeh until golden , it will soak up the oil , this is normal', 'lower the heat when you see them starting to color but fry for a few minutes more , add the soy mix , stir to coat and add to the vegetables , mix', 'by now the tomatoes should have made a sauce and have dissolved', 'taste to see if you want to add more sambal and / or sugar', 'now add the bean sprouts , cook further until you see them go limp', 'transfer to serving dish , sprinkle with 2 tablespoons of the coconut and peanut mixture , serve with rice and the remaining topping on the side'], 'if you do not like it too hot, leave out the chili peppers and sambal, it will still be a spicy indonesian inspired dish.', ARRAY['onion', 'garlic', 'ground coriander', 'ground cumin', 'red chili pepper', 'peanut oil', 'carrots', 'green beans', 'tomatoes', 'vegetable stock', 'bean sprouts', 'tempeh', 'soy sauce', 'ground cayenne pepper', 'sambal oelek', 'ginger', 'brown sugar', 'water', 'desiccated coconut', 'peanuts'], 20, '/static/88.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (92, 'steamed  chicken cutlets in packages', '98930', 35, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "very-low-carbs", "main-dish", "poultry", "oven", "dinner-party", "chicken", "dietary", "low-sodium", "low-saturated-fat", "high-protein", "low-carb", "inexpensive", "high-in-something", "low-in-something", "meat", "chicken-breasts", "equipment"]', ARRAY[190.5, 12.0, 3.0, 3.0, 53.0, 6.0, 0.0], 11, ARRAY['preheat oven to 450f', 'rinse the chicken breasts , and either cut or pound the chicken to create cutlets of about equal size', 'tear off squares of aluminum foil and place each chicken breast on a square', 'slice the tomato and place a slice or two atop of each breast', 'drizzle the chicken with the olive oil', 'sprinkle the basil on top of the chicken breasts evenly', 'add salt and pepper to each breast', 'loosely fold up the foil over each cutlet , leaving both room for air space but folded up enough not to allow oil to drip out', 'place all packages in a large baking dish and bake for 20 minutes', 'serve while still packaged', 'allow each diner to open his / her package at the table'], 'this creates a great aroma when the packages are first opened at the table--the packages hold the flavors in the chicken. im a huge chicken fan, and this is a very simple but tasty way to serve it.', ARRAY['boneless skinless chicken breast', 'tomatoes', 'dried basil', 'olive oil', 'lime juice', 'salt and pepper'], 6, '/static/92.jpg');
