INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (103, 'truth serum  margaritas', '45859', 30, '["30-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "beverages", "mexican", "easy", "cocktails", "dietary", "novelty", "number-of-servings", "3-steps-or-less"]', ARRAY[72.0, 0.0, 61.0, 0.0, 0.0, 0.0, 6.0], 3, ARRAY['mix limeade , lime juice , tequila and triple sec in a pitcher and refrigerate', 'serve in salted glasses with plenty of ice--skip the blender , just serve on the rocks', 'garnish with lime wedge& a splash of grand marnier'], 'you will know why i call it truth serum after you have finished one of these ! be careful, they are strong. i avoid margarita mixes, because they seem to taste so artificial--there is no substitute for fresh lime juice. when limes are on sale, i buy lots and juice them all. i freeze the juice in 12 oz water bottles, so its ready to go for ', ARRAY['limeade', 'lime juice', 'tequila', 'triple sec', 'lime wedge', 'grand marnier'], 6, '/static/103.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (104, 'turtle  squares', '58651', 30, '["30-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "for-large-groups", "desserts", "fruit", "oven", "easy", "finger-food", "kid-friendly", "cookies-and-brownies", "chocolate", "bar-cookies", "nuts", "dietary", "low-sodium", "low-in-something", "taste-mood", "sweet", "equipment", "number-of-servings", "presentation"]', ARRAY[172.4, 16.0, 51.0, 1.0, 2.0, 26.0, 6.0], 15, ARRAY['preheat oven to 350 degrees f', 'spray a 13 x 9 baking pan evenly with non-stick cooking spray', 'beat 1 cup brown sugar with 1 / 2 cup melted butter with an electric mixer on medium for 2-3 minutes', 'add the flour mixture and mix until smooth', 'press the flour mixture evenly and firmly into the prepared baking pan', 'sprinkle the pecans evenly oven the flour mixture in the pan', 'mix the 2 / 3 cup butter and 1 / 2 cup brown sugar in a saucepan and bring to a boil over a medium heat', 'stir constantly while boiling for about 1 minute', 'spread the boiling mixture evenly over the pecans', 'bake for about 20 minutes', 'cool', 'melt the chocolate chips in the microwave , stirring ever 30 seconds or so until almost smooth', 'remove the chocolate chips , stir thoroughly to smooth and spread evenly over the top of the squares', 'chill for 15 minutes until the chocolate sets and then remove from fridge', 'when squares have returned to room temperature cut into 36 squares'], 'for lovers of pecans and chocolate...', ARRAY['flour', 'brown sugar', 'butter', 'pecans', 'semi-sweet chocolate chips'], 5, '/static/104.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (107, 'wacky cake', '40237', 40, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "low-protein", "desserts", "american", "oven", "easy", "beginner-cook", "potluck", "dinner-party", "kid-friendly", "picnic", "cakes", "chocolate", "dietary", "low-cholesterol", "comfort-food", "healthy-2", "low-in-something", "taste-mood", "to-go", "equipment", "number-of-servings"]', ARRAY[2252.6, 130.0, 802.0, 101.0, 44.0, 54.0, 117.0], 7, ARRAY['sift together and make 3 holes in dry ingredients', 'in first hole , pour vegetable oil', 'in second hole , pour vinegar', 'in third hole , pour vanilla', 'pour cold water over all', 'mix well with fork or whisk , but do not beat', 'bake in greased 8 x 8 pan for about 35 minutes at 350 deg f'], 'this is moms favorite chocolate cake. easy to make and has a little different twist. also has no eggs or dairy products. pick your favorite frosting for the top--mom likes 7-minute the best.', ARRAY['flour', 'sugar', 'cocoa', 'baking soda', 'salt', 'vegetable oil', 'vinegar', 'vanilla', 'water'], 9, '/static/107.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (115, '1 french onion soup', '144952', 100, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "soups-stews", "vegetables", "fall", "winter", "seasonal", "oamc-freezer-make-ahead", "onions", "number-of-servings", "4-hours-or-less"]', ARRAY[1268.7, 63.0, 70.0, 96.0, 103.0, 88.0, 57.0], 13, ARRAY['heat the butter and 2 tb olive oil in a large pan until melted', 'add the onions and sugar', 'cook over medium heat , stirring occasionally until they are "caramalized"', 'add the flour and stir for about 3 minutes', 'add the beef broth , and cook the soup over low-medium heat , partly covered , for about 45 minutes', 'add the sherry , cook for an additional 5 minutes', 'preheat the oven to 350 degrees', 'cut the french bread into 1" thick slices', 'bake the bread on an un-greased cookie sheet for 15 minutes', 'brush the bread with olive oil and rub with the cut side of the garlic', 'turn the bread slices over and bake an additional 15 minutes', 'ladle the soup into oven proof crocks , top each with a slice of bread and sprinkle on parmesan , romano and swiss cheese', 'bake the soup for about 10 minutes or until the cheese begins to brown'], 'by far the best onion soup i have ever eaten...and i love food! it freezes well..just freeze the soup and prepare the bread separately.  it sounds like a lot of work but its not, and it is well worth it.  the recipe is supposed to make 8 servings...but in our house it only makes 4!  the measurements for cheese can be altered depending on your tastes.', ARRAY['butter', 'olive oil', 'yellow onions', 'sugar', 'flour', 'beef broth', 'low sodium beef broth', 'sherry wine', 'french bread', 'swiss cheese', 'parmesan cheese', 'romano cheese', 'garlic cloves'], 13, '/static/115.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (117, '1 squash dressing', '31835', 50, '["60-minutes-or-less", "time-to-make", "main-ingredient", "cuisine", "preparation", "north-american", "vegetables", "american", "southern-united-states", "dietary"]', ARRAY[157.7, 17.0, 11.0, 20.0, 11.0, 27.0, 2.0], 4, ARRAY['mix all together', 'put in 2 quart casserole dish', 'bake for 30 minutes at 350f', 'enjoy !'], 'even if you hate squash, you will love this. the flavor of the south come to life. try it, you will love it, i promise!', ARRAY['squash', 'cornbread', 'eggs', 'onion', 'butter', 'cream of chicken soup', 'sage'], 7, '/static/117.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (118, '10 can spaghetti sauce   oamc', '197013', 510, '["course", "cuisine", "preparation", "for-large-groups", "sauces", "main-dish", "condiments-etc", "european", "italian", "dietary", "low-cholesterol", "low-calorie", "low-carb", "savory-sauces", "low-in-something", "number-of-servings"]', ARRAY[165.7, 14.0, 30.0, 46.0, 15.0, 14.0, 4.0], 13, ARRAY['brown sausage in pan , crumbling with spatula , until no pink remains', 'drain and rinse', 'put into a 5 quart crockpot', 'saute onions and garlic in olive oil until translucent and golden but not brown', 'add all the spices and mix well', 'turn off the heat and let the spices mingle for about 10 minutes', 'the heat will let the oils release from the dried herbs', 'add the tomato sauce and spice mixture to the meat in the crockpot and stir to mix', 'cook on high for 1 hour and then low for 7-8 hours', 'use over pasta', 'if freezing , cool sauce and pour into quart sized freezer bags', 'remove all the air and place flat on a cookie sheet and put into freezer', 'when frozen , they will stand up like records in the freezer'], 'i really did make up this recipe on my own, with the help of an italian lady through emails.  the mingling of the oil/spices is the key.  we were really into once a month cooking for awhile and this sauce is/was a hit.', ARRAY['tomato sauce', 'italian sausage', 'olive oil', 'white onion', 'bottled garlic', 'sugar', 'paprika', 'dried parsley', 'dried oregano', 'dried basil', 'garlic powder', 'bay leaves', 'salt', 'pepper'], 14, '/static/118.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (120, 'millionaire pound cake', '286009', 120, '["time-to-make", "course", "cuisine", "preparation", "occasion", "north-american", "desserts", "american", "southern-united-states", "dinner-party", "holiday-event", "cakes", "dietary", "christmas", "thanksgiving", "low-sodium", "low-in-something", "taste-mood", "sweet", "4-hours-or-less"]', ARRAY[878.3, 63.0, 326.0, 13.0, 20.0, 123.0, 39.0], 7, ARRAY['freheat the oven to 300 degrees', 'grease a 10-inch tube pan with butter , dust the bottom and sides with flour , and set aside', 'in a large mixing bowl , cream the butter and sugar with an electric mixer and add the eggs one at a time , beating after each addition', 'alternately add the flour and milk , stirring till the batter is smooth', 'add the two extracts and stir till well blended', 'scrape the batter into the prepared pan and bake till a cake tester or knife blade inserted in the center comes out clean , about 1 1 / 2 hours', 'cool the cake in the pan on a rack for 5 minutes , then turn it out on the rack to cool completely'], 'why a millionaire pound cake?  because its super rich!  this scrumptious cake is the pride of an elderly belle from jackson, mississippi.  the recipe comes from the glory of southern cooking by james villas.', ARRAY['butter', 'sugar', 'eggs', 'all-purpose flour', 'whole milk', 'pure vanilla extract', 'almond extract'], 7, '/static/120.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (121, '1 000 artichoke hearts', '24514', 25, '["30-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "for-1-or-2", "appetizers", "side-dishes", "eggs-dairy", "american", "easy", "dinner-party", "holiday-event", "kid-friendly", "romantic", "vegetarian", "grains", "eggs", "stove-top", "dietary", "spicy", "comfort-food", "valentines-day", "northeastern-united-states", "pasta-rice-and-grains", "taste-mood", "equipment", "number-of-servings"]', ARRAY[422.5, 25.0, 20.0, 46.0, 34.0, 43.0, 19.0], 6, ARRAY['squeeze as much liquid as possible out of the artichoke hearts and drain them on paper towels', 'roll each heart in egg wash , and then roll in breadcrumbs until well-coated', 'deep fry for one to two minutes until lightly browned and slightly crispy', 'melt butter and garlic in a small , individual saut pan', 'saut hearts for two minutes in the garlic butter , basting each heart and taking care not to burn the butter or garlic', 'serve right in the pan with firm bread such as french or italian'], 'by chef anthony fielding at ponchos wreck restaurant in wilmington, vermont.', ARRAY['artichoke hearts', 'egg', 'unseasoned breadcrumbs', 'oil', 'unsalted butter', 'fresh garlic'], 6, '/static/121.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (122, '1 00 tangy chicken recipe', '42508', 280, '["weeknight", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "main-dish", "poultry", "american", "southern-united-states", "barbecue", "potluck", "holiday-event", "picnic", "summer", "chicken", "dietary", "spicy", "seasonal", "comfort-food", "low-carb", "independence-day", "low-in-something", "meat", "taste-mood", "to-go", "equipment", "grilling"]', ARRAY[969.6, 93.0, 55.0, 83.0, 148.0, 86.0, 7.0], 8, ARRAY['combine vinegar , worcestershire , chili powder , salt , pepper , and hot pepper sauce in a small bowl', 'you will have mixed all ingredients except bbq sauce', 'place chicken pieces in a large plastic bag and pour mixture over chicken', 'marinate in refrigerator for at least 4 hours , turning several times', 'spray grill with non stick cooking spray', 'grill chicken pieces over medium heat in covered grill for 30-45 minutes', 'turn occasionally while grilling', 'baste with your favorite bbq sauce for the last 10 minutes of grilling time'], 'this is one of our all time favorite chicken bbq recipes. the recipe produces a spicy moist, flavorful bbq chicken. you might say its one of our signature bbq recipes. the idea for the chicken came out of a bargain bin. the store was selling 3 cookbooks for a dollar and naturally i couldnot pass that up. i found a bbq recipe that had possibilities. since we like spicy food we jazzed it up a bit and we got a great bbq chicken. prep time includes marinating time.', ARRAY['chicken', 'cider vinegar', 'worcestershire sauce', 'chili powder', 'salt', 'black pepper', 'hot red pepper sauce', 'barbecue sauce'], 8, '/static/122.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (124, '100 chocolate cake', '124286', 35, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "low-protein", "desserts", "american", "easy", "beginner-cook", "potluck", "vegetarian", "cakes", "chocolate", "dietary", "comfort-food", "inexpensive", "low-in-something", "taste-mood", "sweet", "to-go"]', ARRAY[3987.2, 245.0, 1306.0, 260.0, 106.0, 369.0, 205.0], 5, ARRAY['mix all cake ingredients till well blended', 'bake in 2 greased round cake pans in 350f oven for 30 minutes', 'cool , while cake cools completely mix filling and spread between cooled cake rounds and frost with thats fudge frosting', 'must refrigerate if serve with pudding filling', 'prep time does not include cooling time'], 'the mayonnaise in this may sound strange, but it it the "secret ingredient" that makes the cake so moist. i recommend not using miracle whip as it contains spices and flavorings that might alter the taste. they make great cupcakes too.', ARRAY['flour', 'sugar', 'baking soda', 'cocoa powder', 'salt', 'mayonnaise', 'water', 'vanilla', 'instant chocolate pudding mix', 'cool whip', 'milk'], 11, '/static/124.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (126, '2000 meatloaf', '475785', 90, '["time-to-make", "course", "main-ingredient", "preparation", "main-dish", "potatoes", "vegetables", "4-hours-or-less", "meatloaf", "simply-potatoes2"]', ARRAY[267.0, 30.0, 12.0, 12.0, 29.0, 48.0, 2.0], 17, ARRAY['pan fry bacon , and set aside on a paper towel to absorb excess grease', 'mince yellow onion , red bell pepper , and add to your mixing bowl', 'chop garlic and set aside', 'put 1tbsp olive oil into a saut pan , along with chopped garlic , teaspoons white pepper and a pinch of kosher salt', 'bring to a medium heat to sweat your garlic', 'preheat oven to 350f', 'coarsely chop your baby spinach add to your heated pan , stir frequently for approximately 5 min to wilt', 'add your spinach to the mixing bowl', 'chop your now cooled bacon , and add it to the mixing bowl', 'add your meatloaf mix to the bowl , with one egg and mix till thoroughly combined', 'add your goat cheese , one egg , 1 / 8 tsp white pepper and 1 / 8 tsp of kosher salt and mix till thoroughly combined', 'transfer to a 9x5 meatloaf pan , and cook for 60 min or until the internal temperature is at least 160f', 'let stand for 5min', 'melt 1tbsp unsalted butter into a frying pan , and cook up to three eggs at a time', 'crack each egg into a separate dish , in order to prevent egg shells from reaching the pan , then add salt and pepper to taste', 'wait until the egg whites are firm looking , but slightly runny on top before flipping your eggs', 'after flipping , wait 10~20 seconds before removing each egg and placing it over your slices of meatloaf'], 'ready, set, cook! special edition contest entry: a mediterranean flavor inspired meatloaf dish. featuring: simply potatoes - shredded hash browns, egg, bacon, spinach, red bell pepper, and goat cheese.', ARRAY['meatloaf mixture', 'unsmoked bacon', 'goat cheese', 'unsalted butter', 'eggs', 'baby spinach', 'yellow onion', 'red bell pepper', 'simply potatoes shredded hash browns', 'fresh garlic', 'kosher salt', 'white pepper', 'olive oil'], 13, '/static/126.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (127, '25 pumpkin pie', '17265', 80, '["weeknight", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "pies-and-tarts", "desserts", "vegetables", "american", "oven", "fall", "heirloom-historical", "holiday-event", "vegetarian", "winter", "pies", "food-processor-blender", "dietary", "christmas", "thanksgiving", "seasonal", "comfort-food", "inexpensive", "taste-mood", "equipment", "small-appliance", "4-hours-or-less"]', ARRAY[350.0, 27.0, 109.0, 13.0, 12.0, 38.0, 13.0], 19, ARRAY['adjust an oven rack to lowest position and heat oven to 400f', 'prepare pie crust for blind baking', 'bake crust for 15 minutes', 'remove parchment from crust , prick any bubbles with a sharp fork and bake shell for 8 to 10 minutes longer , or until bottom just begins to color', 'remove crust from oven and brush lightly with egg white while still hot', 'process pumpkin , sugar , spices and salt ingredients in a food processor fitted with steel blade for 1 minute', 'transfer mixture to a 3-quart heavy saucepan', 'bring it to a sputtering simmer over medium-high heat', 'cook pumpkin , stirring constantly , until thick and shiny , about 5 minutes', 'as soon as pie shell comes out of oven , whisk heavy cream and milk into pumpkin and bring to a bare simmer', 'process eggs in food processor until whites and yolks are mixed , about 5 seconds', 'gradually pour hot pumpkin mixture through feed tube while still running', 'process 30 seconds', 'pour warm filling into hot pie shell', 'put a crust shield on completed pie and bake about 25 minutes', 'filling will be dry-looking and lightly cracked around the edge', 'the center will wiggle when pie is gently shaken', 'cool on a wire rack to a warm room temperature', 'serve with slightly sweetened whipped cream'], 'a unique baking method preserves crispness of the crust and yields a slightly spicy, not too sweet pumpkin pie. the name came shortly after someone purchased three of them from me for $75 :-)', ARRAY['pie crusts', 'egg white', 'pumpkin puree', 'dark brown sugar', 'ground ginger', 'ground cinnamon', 'nutmeg', 'ground cloves', 'salt', 'heavy cream', 'milk', 'eggs'], 12, '/static/127.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (131, '300 icing', '208179', 30, '["30-minutes-or-less", "time-to-make", "course", "preparation", "low-protein", "desserts", "cakes", "dietary", "cake-fillings-and-frostings", "low-in-something"]', ARRAY[339.0, 37.0, 100.0, 7.0, 3.0, 76.0, 10.0], 9, ARRAY['this first part is best made ahead to allow for cooling', 'in a small sauce pan , mix flour into milk , then heat on low heat , stirring constantly until mixture resembles a thick pudding', 'cover and let cool', 'in a separate bowl , cream butter with sugar', 'add vanilla and blend until smooth', 'add first mixture after fully cooled', 'decorate as usual', 'should be able to ice one 8x8 cake', 'excellent for carrot cake when using cream cheese'], 'do not know where this recipe originated, just that its been around my family for a long time.  a very different method of making an icing, however, if you do not like the traditional powdered sugar ones, this is very light & does not taste as sweet.  it is fantastic and a little thicker when cream cheese is substituted for butter.', ARRAY['milk', 'flour', 'butter', 'granulated sugar', 'vanilla'], 5, '/static/131.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (134, '500 margarita bread', '136602', 90, '["time-to-make", "course", "preparation", "occasion", "breads", "dietary", "gifts", "quick-breads", "4-hours-or-less"]', ARRAY[388.7, 32.0, 100.0, 20.0, 11.0, 42.0, 15.0], 16, ARRAY['in a small bowl , combine dates , tequila , orange liqueur , water , lemon and lime zest', 'set aside', 'in large mixer bowl , beat sugar , shortening and egg until fluffy', 'in another bowl , combine flour , soda and salt', 'add to creamed mixture alternately with date mixture , using low to medium speed', 'stir in pecans', 'turn into greased 9" x 5" by 2-1 / 2" loaf pan', 'bake at 350 degrees for 50-60 minutes , or until toothpick inserted in center comes out clean', 'or , bake 3 or 4 mini loaf pans 30-35 minutes', 'turn out on wire rack to cool', 'slice thin and serve with cream cheese spread', 'cream cheese spread:', 'in small mixer bowl , beat cream cheese , butter and sugar until smooth', 'stir in liqueur and lime zest', 'makes 1-1 / 4 cups', 'bread freezes well'], 'this is probably #2 of my top ten all time favorites.  the recipe won harold freeman of phoenix $150 in a newspaper contest probably 20 years ago.  he should have easily won $500, as i have made many life long friends equally addicted to harolds tipsy bread. i usually make it in mini foil pans for gifts. do not skip the special cheese spread, as it really adds the lagniappe!  try it soon, and maybe we can each chip in another $5 to bring harold his just reward for a great recipe!', ARRAY['pitted dates', 'tequila', 'orange-flavored liqueur', 'triple sec', 'warm water', 'lemon zest', 'lime zest', 'brown sugar', 'shortening', 'egg', 'all-purpose flour', 'baking soda', 'salt', 'pecans', 'cream cheese', 'butter', 'powdered sugar'], 17, '/static/134.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (141, 'rtsoppa  swedish yellow pea soup', '428056', 150, '["time-to-make", "course", "main-ingredient", "cuisine", "preparation", "soups-stews", "vegetables", "scandinavian", "european", "swedish", "4-hours-or-less"]', ARRAY[33.2, 0.0, 12.0, 25.0, 1.0, 0.0, 2.0], 14, ARRAY['rinse and pick through the peas', 'if using whole peas , allow to sit in the water you will use for your soup overnight', 'this is unnecessary if you are using split peas as long as you simmer the soup for 2-3 hours', 'fill a large pot with 8 cups water', 'add the peas , chopped onions , whole onion with cloves , carrot , and ham bone to the pot', 'bring to a boil , then simmer over low heat for 90 minutes', 'if using whole peas , skim off any pea skins that have risen to the surface', 'remove 2-3 cups of the soup , pure it , then return the pure to the pot', 'continue to simmer at least another 30 minutes', '30 minutes before serving , remove the studded onion and the meat', 'chop the meat and return to the pot', 'season with thyme , ginger , salt , and pepper', 'simmer 15 more minutes', 'serve with mustard on the side so it may be added to taste'], 'according to the site where i found this recipe, this soup is traditionally served on thursdays and is usually followed by crepe-like pancakes and whipped cream and preserves for dessert. a teaspoon of grainy brown mustard on top of each serving helps to enhance the flavors.', ARRAY['dried yellow peas', 'water', 'onions', 'clove', 'carrot', 'ham bone', 'dried thyme', 'ground ginger', 'salt', 'pepper', 'brown mustard'], 11, '/static/141.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (144, 'go to bbq sauce for ribs', '495314', 13, '["15-minutes-or-less", "time-to-make", "course", "cuisine", "preparation", "north-american", "sauces", "condiments-etc", "american", "easy", "3-steps-or-less"]', ARRAY[233.5, 0.0, 209.0, 52.0, 2.0, 0.0, 19.0], 3, ARRAY['mix all ingredients together', 'heat on medium heat in a saucepan until bubbly', 'turn down and keep warm until ready to mop onto your meat'], 'this bbq sauce will stick to your ribs! you can also use it for chicken and pork, heck, just about any meat. if grilling, put sauce on during the last few minutes so it doesnt just burn on your grill. serve additional sauce on the side.', ARRAY['tomato ketchup', 'dark brown sugar', 'applesauce', 'ponzu sauce', 'maple syrup', 'liquid smoke', 'apple cider vinegar', 'salt', 'oregano', 'granulated garlic', 'cayenne'], 11, '/static/144.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (149, 'anything goes  low fat mushroom sauce', '29214', 35, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "healthy", "sauces", "condiments-etc", "vegetables", "easy", "low-fat", "stove-top", "dietary", "low-cholesterol", "low-saturated-fat", "low-calorie", "comfort-food", "low-carb", "savory-sauces", "mushrooms", "low-in-something", "taste-mood", "equipment"]', ARRAY[153.4, 7.0, 29.0, 22.0, 17.0, 6.0, 4.0], 15, ARRAY['in large non-stick skillet , saut mushrooms and garlic in margarine until tender', 'add 1 1 / 4 cup broth , rosemary , and thyme', 'bring to boil', 'cook until reduced by two-thirds', 'add 2 / 3 cup broth , sherry or additional broth , teriyaki , and hot pepper sauce', 'cook and stir over high heat until mixture is reduced by two-thirds', 'add remaining broth , reduce heat , and simmer 5 minutes', 'in bowl , combine milk powder and cornstarch', 'gradually stir in milk until well blended', 'stir milk mixture into sauce', 'bring to boil', 'cook and stir for 2 minutes , or until thickened', 'reduce heat , add cheese , mustard , and oregano', 'stir until cheese is melted', 'for vegetarian use the vegetable broth'], 'this tasty low-fat sauce goes great with anything!! from pork chops, to chicken, to pasta, to baked potatoes...it is yummy!! i think i got the basis for this recipe from "light & tasty" magazine and tweaked it a bit here and there.', ARRAY['fresh mushrooms', 'garlic cloves', 'margarine', 'chicken broth', 'dried rosemary', 'dried thyme', 'dry sherry', 'teriyaki sauce', 'hot pepper sauce', 'nonfat dry milk powder', 'cornstarch', 'nonfat milk', 'parmesan cheese', 'spicy brown mustard', 'dried oregano', 'salt and black pepper'], 16, '/static/149.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (150, 'atkins friendly  cheesecake', '92533', 105, '["time-to-make", "course", "preparation", "desserts", "oven", "cheesecake", "dietary", "equipment", "4-hours-or-less"]', ARRAY[397.7, 57.0, 5.0, 11.0, 21.0, 90.0, 2.0], 21, ARRAY['for the crust:', 'preheat oven to 350f', 'put 1 1 / 2 cups of almonds in blender and grind to medium / fine texture', 'add 1 / 3 cup of vanilla flavored whey protein powder', 'add 4 tablespoons of melted butter', 'add 1 tablespoon of cinnamon and 1 tablespoon of nutmeg', 'add about 3 tablespoons of splenda and blend', 'spray pie dish with non-stick spray and pour in mixture', 'use your hands to make the crust', 'cook for 12- 15 minutes', 'let cool', 'for the cheesecake:', 'heat oven to 325f', 'put cream cheese in a large bowl and mix until smooth', 'beat in splenda and sour cream and mix well', 'beat in vanilla', 'beat in eggs one at a time', 'beat until very smooth , no lumps', 'pour into crust and put in oven with a pan of water on the lower rack', 'bake 1 hour', 'chill before eating'], 'my neighbor frannie came up with the idea for this cheesecake. her husband and mine (who are on the atkins diet) both love it! this cake is sweetened with splenda. makes 12 servings, 6 to 8 grams of carbs each.', ARRAY['almonds', 'vanilla protein powder', 'butter', 'cinnamon', 'nutmeg', 'splenda sugar substitute', 'cream cheese', 'sour cream', 'vanilla', 'eggs'], 10, '/static/150.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (155, 'berry good  french toast bake', '236184', 45, '["60-minutes-or-less", "time-to-make", "course", "preparation", "occasion", "breakfast", "brunch"]', ARRAY[682.6, 45.0, 43.0, 43.0, 47.0, 63.0, 26.0], 10, ARRAY['generously grease 2 1 / 2 quart casserole of rectangular baking dish , 13 x 9', 'beat flour , milk , sugar , vanilla , salt , and eggs in a large bowl with a hand beater until smooth', 'stir in bread cubes until coated', 'pour bread mixture into pan', 'top evenly with cream cheese , berries , and nuts', 'cover and refrigerate up to 24 hours', 'heat oven to 400', 'uncover and bake 20 - 25 minutes or until', 'golden brown', 'sprinkle with powdered sugar and serve with syrup'], 'from a friend...', ARRAY['flour', 'milk', 'sugar', 'vanilla', 'salt', 'eggs', 'french bread', 'cream cheese', 'frozen blueberries', 'nuts', 'powdered sugar', 'blueberries', 'maple syrup'], 13, '/static/155.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (159, 'better than tofu  cheesecake', '200148', 90, '["time-to-make", "course", "main-ingredient", "preparation", "desserts", "beans", "vegan", "vegetarian", "cheesecake", "dietary", "soy-tofu", "4-hours-or-less"]', ARRAY[252.6, 24.0, 66.0, 4.0, 8.0, 11.0, 7.0], 18, ARRAY['for crust:', 'melt margarine and blend well with graham cracker cumbs and sweetener', 'press well into bottom of spring-form pan or round cake pan', 'bake at 350f for 10 minutes', 'let cool', 'filling:', 'mash tofu with vegan cream cheese', 'add all other ingredients to tofu mixture and whip with hand mixer until well blended and very creamy', 'try to get all tofu lumps out', 'pour into crust and bake at 350f for 45-50 minutes', 'turn off oven and leave in for 15 minutes more to help set center', 'when done edges should be golden and starting to pull away from sides of pan , center should be soft set', 'cool about an hour then refrigerate another 3 hours to set center', 'keep refrigerated', 'helpful hints: draining the tofu well is important', 'too much liquid and the center wont set well', 'after slicing the cake place a paper towel in the cut out section before returning it to the frige', 'this will absorb any fluid that will condense on the plate or in the pan and keep the bottom of the cake from getting too soggy'], 'this is, again, from vegweb.com. this got absolutely amazing reviews over there, so im guessing its pretty darn good (multiple users said it would even fool the dairy lovers)! a few users suggested only using half the recommended amount of tofu (1 12oz. container). others said they used turbinado sugar in place of the syrup. cannot wait to try it!  lemme know what you think!', ARRAY['graham cracker crumbs', 'vegan sugar', 'margarine', 'soft silken tofu', 'vegan cream cheese', 'frozen pineapple concentrate', 'oil', 'lemon juice', 'vanilla extract', 'maple syrup', 'coriander', 'cornstarch', 'water'], 13, '/static/159.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (160, 'big easy   gumbo', '445026', 68, '["weeknight", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "gumbo", "soups-stews", "seafood", "african", "american", "cajun", "southern-united-states", "easy", "dinner-party", "heirloom-historical", "shrimp", "creole", "dietary", "low-calorie", "oamc-freezer-make-ahead", "low-carb", "low-in-something", "shellfish", "3-steps-or-less", "4-hours-or-less"]', ARRAY[484.1, 42.0, 7.0, 32.0, 75.0, 31.0, 6.0], 11, ARRAY['heat oil in a large dutch oven over medium-high heat', 'gradually whisk in flour and cook , whisking constantly , 5 to 7 minutes or until flour is chocolate colored', 'do not burn mixture', 'reduce heat to medium', 'stir in onion and next 4 ingredients and cook , stirring constantly , 3 minutes', 'gradually stir in chicken broth', 'add chicken and next 2 ingredients', 'increase heat to medium-high and bring to a boil', 'reduce heat to low and simmer , stirring occasionally , 20 minutes', 'add shrimp and cook 5 minutes or just until shrimp turn pink', 'serve over hot rice'], 'from january, 2011s issued of southern living.  this is an easier version of the original because it uses pre-cooked chicken and canned broth.  do not forget...heat your oil good before adding the flour for a flavorful and fast roux.  good for oamc, too.', ARRAY['vegetable oil', 'all-purpose flour', 'onion', 'green bell pepper', 'celery', 'creole seasoning', 'garlic cloves', 'low sodium chicken broth', 'cooked chicken', 'andouille sausage', 'frozen black-eyed peas', 'large raw shrimp'], 12, '/static/160.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (162, 'buckley s  cajun salmon pasta', '273326', 38, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "north-american", "main-dish", "eggs-dairy", "pasta", "seafood", "american", "cajun", "southern-united-states", "easy", "salmon", "fish", "one-dish-meal", "pasta-rice-and-grains", "saltwater-fish"]', ARRAY[1594.9, 98.0, 26.0, 22.0, 149.0, 175.0, 58.0], 16, ARRAY['for the cream sauce:', 'saut minced garlic with butter until garlic is light brown', 'add heavy cream , basil , lemon juice , grated cheese blend , & white pepper', 'bring to a boil on medium high heat , stirring often', 'place on low heat after boiling & salt to taste', 'for the cajun salmon:', 'preheat oven to 375f', 'lightly coat salmon with cajun spice', 'remember , the amount of spice will determine the degree of spice & heat', 'so cook salmon at 375f for 8-15 minutes depending on desired doneness', 'for the presentation:', 'toss cooked pasta with the cream sauce', 'portion 8 ounces of pasta per serving', 'place the cooked salmon filets atop the portions', 'garnish with parsley & shredded parmesan cheese', 'a slice of garlic toast & a lemon wedge will further complement this dish'], 'this is another hit from my uncles restaurant. it is a simply elegant dish, & although it takes time, it will be worth the praise you get!', ARRAY['heavy whipping cream', 'basil', 'parsley', 'garlic', 'lemon juice', 'parmesan-romano cheese mix', 'parmesan cheese', 'butter', 'cajun spices', 'salt', 'white pepper', 'linguine', 'salmon'], 13, '/static/162.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (163, 'burek  or feta cheese  phyllo pie', '310570', 65, '["time-to-make", "course", "main-ingredient", "cuisine", "preparation", "savory-pies", "main-dish", "eggs-dairy", "easy", "european", "cheese", "4-hours-or-less"]', ARRAY[428.1, 29.0, 11.0, 40.0, 37.0, 44.0, 14.0], 38, ARRAY['first - let me explain the feta', 'we have an italian market that sells fresh fruits and veggies etc', 'and they have a fresh cheese section', 'i always buy a block of french feta cheese', 'i use half for this recipe and the rest for omelets etc', 'you can use the prepacked feta but if you want the full experience you will need a lot', 'also , you may use other variety of feta the french is very salty which is why i add cottage cheese', 'it kind of balances out the saltiness abit', 'you just need 1 package of phyllo', 'mine says 32 oz , buy the long box not the country', 'preheat oven to 350f', 'in large mixing bowl beat eggs , add 2 cups milk and mix well', 'take your block of feta and cut in half', 'use one 1 / 2 and refrigerate the other', 'crumble the feta into the egg mix and add cottage cheese', 'make sure you have nice chunks', 'mix well , the mixture should be thick and very lumpy', 'if too thick add more milk', 'take a large deep baking dish', 'sprinkle oil on bottom only', 'lay one sheet phyllo and drizzle with oil , continue until you have 4 sheets', 'when you add the last sheet place your hand flat on the phyllo sheets and gently gather , without tearing by pulling the sides slightly toward center', ':)', 'use 1 full ladle of the egg / feta mixture onto the layer , you do not need to cover all the phyllo just do not leave the feta in one spot distribute', 'layer another phyllo sheet and sprinkle with oil', 'layer another sheet of phyllo and again crumple the center then sprinkle the oil', 'one ladle of the egg / feta mix', 'continue in this pattern', 'if you see that you have alot of the egg / feta mix left you can start to add more than the 1 ladle', 'your last layer should be 3-4 layers of phyllo', 'do not sprinkle with oil yet', 'take a soup spoon and with the handle , tuck in the sides', 'now sprinkle with oil', 'bake for about 30-40 minutes or until the top is puffed and nicely browned', 'your kitchen with smell wonderful', 'the burek will puff high off the top of the pan but once cooled will drop', 'you must cool completely or you will nto be able to cut with out it falling apart', 'its moist and a bit salty and goes great with a nice cold beer ! you can serve as main meal with salad or as appetizer'], 'ok, there are different version of burek (some eastern europeans even greeks) roll a fresh dough and then spend more time rolling, folding and waiting. but if you are like me and my mom, use phyllo! its quick and you can always keep it in the freezer.  i remember while i was growing up and my mom would make this every sunday! the smell alone coming from our kitchen used to make me come running home. please excuse the directions, but the recipe does not really have measurements or written directions! this is an old recipe and no one ever wrote it down.', ARRAY['phyllo dough', 'eggs', 'milk', 'feta', 'oil', 'cottage cheese'], 6, '/static/163.jpg');
