INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (192, 'fire  sauce', '39226', 10, '["15-minutes-or-less", "time-to-make", "course", "cuisine", "preparation", "occasion", "north-american", "low-protein", "healthy", "sauces", "condiments-etc", "mexican", "easy", "low-fat", "stove-top", "dietary", "copycat", "low-cholesterol", "low-saturated-fat", "low-calorie", "low-carb", "savory-sauces", "low-in-something", "novelty", "equipment", "number-of-servings"]', ARRAY[80.5, 1.0, 37.0, 89.0, 6.0, 0.0, 5.0], 8, ARRAY['combine the tomato paste and water in a saucepan', 'whisk until smooth', 'add the remaining ingredients to the tomato mixture', 'heat over medium / high heat until it begins to boil', 'continue to cook , stirring often , for 4 to 5 minutes', 'remove from heat', 'when sauce has cooled , pour into a glass airtight container and refrigerate', 'stores well in the refrigerator'], 'i cannot seem to make enough of this sauce--and i triple the recipe every time. my family uses it on eggs, hash browns, is not and burritos (of course). so, if you like this type of thing, this is the sauce for you!', ARRAY['tomato paste', 'water', 'vinegar', 'canned jalapeno slices', 'chili powder', 'dried onion flakes', 'salt', 'cornstarch', 'cayenne pepper', 'sugar', 'onion powder', 'garlic powder'], 12, '/static/192.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (194, 'flipped  roast turkey', '268169', 195, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "for-large-groups", "very-low-carbs", "main-dish", "poultry", "holiday-event", "turkey", "dietary", "low-sodium", "high-protein", "low-carb", "high-in-something", "low-in-something", "meat", "whole-turkey", "number-of-servings", "4-hours-or-less"]', ARRAY[498.9, 40.0, 3.0, 8.0, 119.0, 41.0, 0.0], 15, ARRAY['preheat oven to 450f', 'remove neck and giblets from cavity of turkey', 'rinse well with cold water and pat dry', 'using a pastry brush , coat entire outside of turkey lightly but well with olive oil', 'rub softened butter over the skin', 'peel onions and slice thickly', 'place onion slices in bottom of turkey roaster to form a bed', 'season turkey lightly all over with herbs and spices of choice', 'place turkey breast side down in roaster', 'cook at 450f for 30 minutes', 'remove from oven', 'reduce temperature to 325 degrees f', 'carefully turn turkey so that breast is facing up', 'return turkey to oven and cook until done', 'allow turkey to rest for at least 30 minutes before carving'], 'of all the recipes i have posted on zaar, one of the most popular is recipe #98395.  for the last several years, i have thought about trying the method described with a turkey, but i always wimped out at the last minute.  since you only get one chance each year to do the thanksgiving feast right, its a little scary to try new stuff.  this time, though, i was on a roll.  since everything else was coming together so well, i finally decided to take a risk on the turkey.when it came out of the oven, 10 people were standing in my kitchen, and they sort of collectively gasped.  one of our (non-family) guests asked, "where did you get that turkey?  and what did you do to it?  it looks like something out of a magazine!"  :)to my absolute delight, it tasted as good as it looked!  everybody said it was the juiciest, most flavorful turkey theyd ever eaten.  so, yeah, "flipping the bird" works even when its the big bird!cook time is a total guess. you will need to check the temp.', ARRAY['whole turkey', 'olive oil', 'butter', 'onions', 'kosher salt', 'pepper', 'italian spices'], 7, '/static/194.jpg');
