INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (199, 'german  barbecued carrots', '109818', 55, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "5-ingredients-or-less", "side-dishes", "vegetables", "easy", "dietary"]', ARRAY[173.5, 16.0, 38.0, 21.0, 8.0, 17.0, 5.0], 7, ARRAY['preheat oven to 350f', 'cook bacon until crisp', 'drain grease and crumble into fine bits', 'peel and slice carrots , place into baking dish', 'add tomato soup , bacon and sugar and stir well', 'bake for 30-45 minutes until bubbly', 'eat and enjoy !'], 'this is something my grandma made at christmas. i do not know where she originally got the recipe but the recipe card for it has her handwriting on it. i call them german carrots because thats grandparents came over from germany. my family mostly eats these at christmas and we usually fight over who gets the little bit thats leftover.  its a really good barbecued carrot recipe.', ARRAY['carrot', 'bacon', 'tomato soup', 'sugar'], 4, '/static/199.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (202, 'golden circle     pineapple upside down cake', '226246', 70, '["time-to-make", "course", "cuisine", "preparation", "south-west-pacific", "desserts", "australian", "cakes", "4-hours-or-less"]', ARRAY[339.6, 20.0, 141.0, 15.0, 7.0, 41.0, 17.0], 15, ARRAY['preheat your oven to 180c', 'pineapple layer:', 'melt the butter and pour into the base of a 20cm round cake tin', 'also brush the butter around the sides of the tin', 'sprinkle the brown sugar over the base of the tin', 'arrange the pineapple rings in the tin and place a cherry into the centre of each ring', 'cake batter:', 'cream the butter and sugar until light and fluffy', 'beat in the egg and the vanilla', 'sift the flour and fold into the mixture , alternating with the milk', 'pour the cake batter over the pineapple and cherries', 'bake for 40 to 50 minutes , or until a skewer inserted into the cake comes out clean', 'remove the cake from the oven and loosen the cake from the sides of the tin', 'invert the cake onto a serving plate', 'enjoy either warm or cold'], 'this is the best pineapple upside-down cake! my mother has the same recipe in a book from the 1970s and the same recipe is still available/posted on the golden circle website currently. golden circle is an australian brand and canned from australian grown products sadly -this recipe i have not yet tried to make as gluten-free.', ARRAY['pineapple in syrup', 'butter', 'brown sugar', 'glace cherries', 'caster sugar', 'egg', 'vanilla essence', 'self-raising flour', 'milk'], 9, '/static/202.jpg');
