INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (208, 'honey  i m peanuts about you   cake', '293225', 70, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "for-large-groups", "desserts", "oven", "easy", "dinner-party", "holiday-event", "cakes", "chocolate", "dietary", "taste-mood", "sweet", "equipment", "number-of-servings", "presentation", "served-cold", "4-hours-or-less"]', ARRAY[1187.4, 137.0, 205.0, 19.0, 55.0, 206.0, 31.0], 39, ARRAY['cooks', 'preheat the oven to 350 degrees f', 'grease 2 round cake pans', 'as the oven preheats , begin to make the cake batter', 'dissolve the cocoa into the espresso', 'stir the buttermilk into the cocoa mixture and set aside to cool', 'whisk together the flour , baking soda and salt', 'in another bowl , beat the butter with an electric mixer at medium speed for about 6 minutes , while gradually adding the sugar', 'the butter mixture should be light and fluffy', 'slowly add the eggs and vanilla to the butter , while continuing to beat', 'be sure to scrape down the sides of the bowl as you go , so all the ingredients get incorporated', 'fold the flour and cocoa mixtures into the butter mixture with a rubber spatula , alternating from flour to cocoa in the following order: 1 / 3 flour mixture', '1 / 2 cocoa mixture', '1 / 3 flour mixture', '1 / 2 cocoa mixture', '1 / 3 flour mixture', 'do not overmix !', 'pour half of the finished batter into each of the cake pans and level the tops with the spatula', 'bake for 30-40 minutes in the center of the oven , rotating halfway through', 'check with a wooden toothpick for doneness', 'cool for at least 15 minutes before inverting onto a baking rack to cool completely', 'make the buttercream: with an electric mixer beat the peanut butter and butter in a large bowl until creamy', 'add the powdered sugar and beat until spreadable', 'with a serrated knife halve the two layers making 4', 'place 1 cake layer top down on the rack and spread 1 / 3 of the buttercream to the edges of the layer', 'top the first cake layer with another and repeat with the buttercream', 'continue layering with the remaining layers and buttercream , ending with cake', 'refrigerate covered in plastic wrap for at least 3 hours and up to overnight', 'remove the cake from the refrigerator and carefully transfer to a wire rack over a sheet pan', 'to make the ganache , chop the butter and scatter it into the chocolate pieces in a large bowl', 'heat the cream and corn syrup in a saucepan over moderate heat', 'as soon as the cream mixture begins to boil , pour it over the chocolate mixture and begin stirring vigorously', 'when the chocolate has melted and is combined thoroughly with the cream , stir in the vanilla', 'quickly begin pouring the ganache over the top of the cake , allowing it to flow down and cover the sides', 'pack the chopped peanuts onto the ganache to cover the sides of the cake , leaving the chocolate on top exposed', 'store in the refrigerator', 'serving suggestions: i like to reserve a little of the buttercream and a handful of whole honey roasted peanuts to decorate the top of the cake', 'try using a star', 'then , set peanuts into the buttercream border to form an interesting design'], 'i saw this recipe showdown cake recipe and had to try it.  dh was like "wow! you have got to try it!"  i told him that i had already had enough of it.  i had to give this away so i wouldnt eat more. lol!  this recipe makes a good size cake so you do not need big pieces. this is a chocolate peanut butter lovers dream! :d', ARRAY['unsweetened cocoa', 'espresso', 'buttermilk', 'unbleached all-purpose flour', 'baking soda', 'salt', 'unsalted butter', 'sugar', 'extra-large eggs', 'pure vanilla extract', 'creamy peanut butter', 'powdered sugar', 'semisweet chocolate', 'heavy cream', 'light corn syrup', 'honey roasted peanuts'], 16, '/static/208.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (209, 'hot tamale  burgers', '235173', 60, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "main-dish", "rice", "vegan", "vegetarian", "dietary", "pasta-rice-and-grains", "brown-rice"]', ARRAY[165.3, 5.0, 4.0, 0.0, 7.0, 2.0, 10.0], 15, ARRAY['bring rice and 3 cups water to a boil in medium sauce pan', 'reduce heat to low , cover and simmer 40 minutes or until water is absorbed by rice', 'meanwhile , heat oil in large skillet over medium heat', 'add onion , bell pepper , corn , chipotle , adobo sauce , garlic , cumin and salt', 'saut 2 minutes , then reduce heat to low', 'cover and cook 15 minutes or until vegetables are tender , stirring once or twice', 'whisk 1 / 2 cup cornmeal in bowl add to skillet mixture and cover and cook on low for 10 minutes more', 'stirring once or twice', 'remove from heat , and stir in hot rice , cilantro , lime juice and zest', 'cool 20 minutes', 'wet hands and shape mixture into 8 burgers', 'refrigerate 30 minutes', 'prepare grill to medium heat', 'brush burgers and grill rack with olive oil', 'grill 7 minutes per side or until crusty on the outside'], 'spicy burgers are vegetarian.', ARRAY['short-grain brown rice', 'olive oil', 'onion', 'red bell pepper', 'corn kernel', 'chipotle chile in adobo', 'garlic cloves', 'ground cumin', 'yellow cornmeal', 'cilantro', 'fresh lime juice', 'lime zest'], 12, '/static/209.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (211, 'i hate miracle whip  but i love this potato salad that s', '220186', 40, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "side-dishes", "potatoes", "vegetables", "american", "picnic", "dietary", "midwestern", "to-go"]', ARRAY[333.4, 20.0, 39.0, 22.0, 17.0, 13.0, 15.0], 5, ARRAY['boil potatoes until tender but not mushy , set aside to cool', 'for dressing: mix all ingredients in a bowl', 'any and all of the ingredients can be adjusted to your taste as my dressing never turns out the same way twice depending on whether im in the mood for sweet , peppery , salty , etc', 'mix potatoes , celery , onion , egg , and dressing in a bowl and chill', 'enjoy !'], 'i have had people who swear they hate miracle whip eat an entire plate of this potato salad.  its my moms recipe, and you can try it with regular mayo, but i wont stand by the results because its so good with the whip that i have never tried it any other way!', ARRAY['potatoes', 'celery', 'white onions', 'hard-boiled eggs', 'miracle whip', 'milk', 'sugar', 'mustard', 'dijon mustard', 'white pepper', 'fresh ground pepper', 'salt'], 12, '/static/211.jpg');
