INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (221, 'just trust me  bistro chicken salad', '345123', 80, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "salads", "rice", "dinner-party", "spring", "summer", "salad-dressings", "seasonal", "pasta-rice-and-grains", "brunch", "4-hours-or-less"]', ARRAY[489.7, 39.0, 54.0, 22.0, 51.0, 19.0, 13.0], 11, ARRAY['marinade', 'create a marinade , by mixing oil , orange juice , vinegar , & salt', 'whisk to blend', 'toss into cooked chicken and allow to marinate for several hours in a plastic bag in the refrigerator before adding other ingredients', 'assembly', 'in a very large bowl , gently toss all ingredients except mayo and reserved juice', 'dressing', 'whisk together mayonnaise and pineapple juice', 'pour over mixed ingredients and toss', 'let rest', 'cover and refrigerate for 1 hour before serving'], 'this incredible mix of unique ingredients creates a cold salad that will melt in your mouth.  it looks, tastes, and serves like a gourmet side dish youd be served at a very expensive garden luncheon, because it was.  my aunt, is an annual winner of the newport beach, ca home and garden tour.  she serves this on the tour to the guests at her 5 million dollar home, and is always prepared with printed recipes to accommodate all the requests.serve this to your next crowd, and you will be the bell of the ball. it serves a large group!', ARRAY['cooked chicken breasts', 'canola oil', 'orange juice', 'white vinegar', 'salt', 'cooked rice', 'green grapes', 'celery', 'pineapple tidbits', 'mandarin oranges', 'slivered almonds', 'mayonnaise'], 12, '/static/221.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (222, 'kelly s creamy cheddar pea salad', '125195', 20, '["30-minutes-or-less", "time-to-make", "course", "cuisine", "preparation", "north-american", "salads", "side-dishes", "american", "easy", "midwestern", "3-steps-or-less"]', ARRAY[173.1, 13.0, 25.0, 11.0, 21.0, 24.0, 4.0], 5, ARRAY['in large bowl , combine mayo , relish , sugar , seas', 'salt and mustard', 'gently fold in rest of ingredients adding peas last so they do not get broken up and mushy', 'chill', 'if you like it extra creamy , double the sauce'], 'im not a big fan of peas, but like them in things. this salad is one of my favorites. happy to share!', ARRAY['frozen peas', 'celery', 'hard-boiled eggs', 'onion', 'cheddar cheese', 'mayonnaise', 'sweet pickle relish', 'sugar', 'seasoning salt', 'mustard'], 10, '/static/222.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (223, 'kicked up  oven fried chicken', '141939', 345, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "main-dish", "eggs-dairy", "poultry", "vegetables", "oven", "easy", "refrigerator", "dinner-party", "holiday-event", "chicken", "dietary", "spicy", "comfort-food", "meat", "onions", "taste-mood", "equipment"]', ARRAY[215.0, 17.0, 14.0, 15.0, 13.0, 22.0, 7.0], 13, ARRAY['in a large bowl whisk the buttermilk , oil , hot pepper sauce , minced garlic , dijon mustard , 1 teaspoon salt and about 1 / 2 teaspoon black pepper', 'add in sliced onion and chicken pieces', 'turn to coat / mix well', 'cover and refrigerate for a minimum of 4 hours or up 24 hours , turning occasionally', 'place a cookie rack on a foil-lined 15x10-inch baking sheet', 'in a bowl mix together dry breadcrumbs , parmesan cheese , flour , thyme spice , paprika , cayenne pepper , 1-1 / 2 teaspoon seasoned salt salt and garlic powder', 'remove the chicken pieces from marinade , allowing any excess marinade to drip off the chicken', 'dip the chicken pieces into the breadcrumb mixture and turn to coat completely', 'arrange chicken on rack or racks on the baking sheets', 'let chicken stand for 30 minutes , no more no less !', 'set oven to 425f', 'drizzle the melted butter over the chicken pieces', 'bake until crisp , golden and cook through about 45 minutes'], 'i make mine on one cookie rack placed on a baking sheet but im sure that the chicken could still be great placed directly on a foil-lined baking sheet, although i do not think it would be as crispy. plan ahead the chicken needs to soak in the buttermilk for a minimum of 4 hours or overnight. if you like a lot of heat then use 2 tablespoons of hot pepper sauce, or you can omit the hot pepper sauce completely, but i suggest adding it in, it really brings out the kick in this chicken---- you will *love* this recipe, i know my family does! :)', ARRAY['buttermilk', 'oil', 'hot pepper sauce', 'dijon mustard', 'fresh garlic', 'seasoning salt', 'black pepper', 'onions', 'chicken pieces', 'dry breadcrumbs', 'parmesan cheese', 'all-purpose flour', 'dried thyme', 'paprika', 'cayenne pepper', 'garlic powder', 'butter'], 17, '/static/223.jpg');
