INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (230, 'lemon coconut custard  pie', '130819', 60, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "pies-and-tarts", "desserts", "eggs-dairy", "fruit", "vegetables", "summer", "nuts", "eggs", "seasonal", "coconut", "squash"]', ARRAY[310.3, 19.0, 154.0, 7.0, 6.0, 29.0, 15.0], 5, ARRAY['whisk together squash , sugar , eggs , flour , lemon and coconut flavorings , and butter', 'pour into pie crust', 'bake at 350 f for 30-45 minutes', 'pie should be set and barely golden brown on top', 'can be served with cool-whip or other favored whipped topping'], 'using zucchini was always a challenge at hom in pa dutch country. this delightful dessert pie has a tropical flair!  do not let the ingredient list scare you away. i have served this to people who do not like squash and they never know if you do not tell them.  if using zucchini squash, peel the green first for the best disguise.', ARRAY['squash', 'sugar', 'eggs', 'flour', 'lemon flavoring', 'coconut flavoring', 'butter', 'pie crust', 'cool whip'], 9, '/static/230.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (237, 'margarita  dip for fresh  or grilled fruit', '250883', 70, '["time-to-make", "course", "main-ingredient", "preparation", "for-large-groups", "5-ingredients-or-less", "appetizers", "lunch", "snacks", "eggs-dairy", "easy", "beginner-cook", "finger-food", "dips", "cheese", "dietary", "number-of-servings", "presentation", "served-cold", "4-hours-or-less"]', ARRAY[84.3, 11.0, 15.0, 2.0, 2.0, 20.0, 1.0], 14, ARRAY['beat cream cheese , limeade concentrate and orange juice in medium bowl with electric mixer on medium speed until well blended', 'gently stir in cool whip topping', 'cover', 'refrigerate at least one hour', 'serve with cut-up fresh fruit', 'to grill fruit:', 'peaches , nectarines and plums get a deep caramel-like flavor when cooked on the grill', 'heres how to do it', 'put pieces of fresh fruit on skewers', 'use fruit thats just staring to soften , so that it stays firmly on the skewers', 'sprinkle fruit with cinnamon or nutmeg', 'place fruit on the grill for a minute to sear', 'check the underside', 'once its golden with grill marks , turn it over to finish cooking'], 'this cool and creamy dip is a refreshing treat for a hot summer day. serve it with cut up fresh or grilled fruit. from kraft kitchens.', ARRAY['cream cheese', 'frozen limeade concentrate', 'orange juice', 'cool whip topping'], 4, '/static/237.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (239, 'middle eastern   twice baked potatoes', '120297', 110, '["time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "healthy", "side-dishes", "potatoes", "vegetables", "asian", "middle-eastern", "dinner-party", "low-fat", "vegetarian", "lebanese", "dietary", "low-sodium", "low-cholesterol", "low-saturated-fat", "low-calorie", "healthy-2", "low-in-something", "4-hours-or-less"]', ARRAY[327.6, 11.0, 16.0, 14.0, 21.0, 6.0, 18.0], 14, ARRAY['preheat the oven to 200c', 'wash the potatoes to remove any dirt , pat dry with paper towels', 'prick the potatoes all over with a fork , brush with olive oil and season to taste with salt and pepper', 'place the potatoes on a lightly greased baking tray and bake for about an hour or until cooked through', 'cool for about 10 minutes', 'while the potatoes are roasting , drain the chickpeas , place in a large mixing bowl and mash with a fork', 'add the ground coriander , the cumin , the crushed garlic and half the fresh coriander', 'cover the bowl with with plastic wrap , and set aside', 'once they have cooled , taking care to keep the shells intact , halve the cooked potatoes and scoop out the flesh', 'mash the potato flesh until smooth and gently mix with the chickpea mixture', 'fold in the yoghurt and tahini , and add a little more of either of these if the mixture seems too dry , season with the pepper and salt , to taste', 'place the potato shells on a lightly greased baking tray and fill them with the potato and chickpea mixture', 'place the filled potato shells in the oven and bake for 10-15 minutes , or until heated through', 'serve the potatoes with the remaining half of the chopped coriander'], 'a delicious yet low-fat twice-baked potato side dish with a blend of classic middle eastern flavours: chickpeas, cumin and coriander.  serve with roasts or with your favourite middle eastern main course dish.  adapted from "practical cookery: low fat".  these potatoes could also be served on their own as a light vegetarian meal. in view of the comments by reviewers about the potatoes being a bit dry, i have added a tablespoon of tahini. i have also added some garlic. im not sure how the recipe escaped having garlic in it when i first posted it!', ARRAY['baking potatoes', 'olive oil', 'chickpeas', 'coriander', 'ground cumin', 'fresh coriander', 'garlic cloves', 'low-fat yogurt', 'tahini', 'salt & freshly ground black pepper'], 10, '/static/239.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (240, 'more lish ous  cornbread', '130492', 40, '["60-minutes-or-less", "time-to-make", "course", "preparation", "occasion", "breads", "easy", "beginner-cook", "dietary", "comfort-food", "taste-mood"]', ARRAY[510.8, 33.0, 82.0, 35.0, 18.0, 17.0, 24.0], 6, ARRAY['preheat oven to 425f', 'in a mixing bowl , beat the eggs , mayo , buttermilk and oil until smooth', 'combine the cornmeal , sugar , baking powder and salt', 'add to the egg mixture and beat until just combined', 'pour into a large greased cast iron skillet or greased round baking dish', 'bake at 425f for 25-30 minutes or until a toothpick inserted into the center comes out clean'], 'when asked how he like the cornbread my best friends hubby smiled big and said its more-lishous well the name stuck and now thats what i call it.  hope you enjoy as much as we do!  sugar can be adjusted from 1/2 to 3/4 cup of sugar..my family likes sweet cornbread.', ARRAY['eggs', 'mayonnaise', 'buttermilk', 'vegetable oil', 'yellow cornmeal', 'sugar', 'baking powder', 'salt'], 8, '/static/240.jpg');
