INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (245, 'no name   beef recipe   loco moco', '101115', 30, '["30-minutes-or-less", "time-to-make", "course", "preparation", "for-1-or-2", "5-ingredients-or-less", "main-dish", "easy", "dietary", "one-dish-meal", "number-of-servings"]', ARRAY[1040.1, 68.0, 16.0, 55.0, 125.0, 86.0, 31.0], 11, ARRAY['cook rice according to package directions', 'divide ground beef in half and make two patties seasoned to your own taste', 'cook patties in a large skillet until done and then set aside leaving fat in pan', 'cook onions in pan until tender and set aside', 'fry both eggs in same pan until done , but with soft centers', 'heat gravy in sauce pan or microwave oven', 'use two dinner plates , making a bed of the cooked rice on each', 'place one of the beef patties on each bed of rice', 'divide cooked onions and place each half around each of the patties', 'top each patty with one of the fried eggs', 'divide beef gravy and pour each half over the plates of food'], 'i saw this dish on a tv travel channel. it was served at an up scale diner on one of the hawian isles. i recall the name of the diner as being mikes, but i do not remember the name of this dish!', ARRAY['ground beef', 'white rice', 'beef gravy', 'onion', 'eggs'], 5, '/static/245.jpg');
