INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (253, 'pasta  with shrimp   eggplant', '223349', 50, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "main-dish", "seafood", "vegetables", "easy", "diabetic", "shrimp", "dietary", "low-sodium", "low-saturated-fat", "low-calorie", "low-carb", "low-in-something", "shellfish"]', ARRAY[283.5, 16.0, 25.0, 11.0, 52.0, 8.0, 7.0], 7, ARRAY['heat oil in a nonstick skillet over medium high heat and add eggplant and cook until almost soft', 'bake spaghetti squash at 450f for 1-2 hours until firm to the touch , let cool and then scoop out pulp', 'add peppers , onions and oregano to eggplant and saut until tender', 'stir in shrimp', 'cover and steam , stirring every so often', 'add in spaghetti squash and cook for less than a minute', 'add olives and parsley: toss again & serve !'], 'a nice low carb way to feel like you are eating pasta!', ARRAY['olive oil', 'eggplants', 'spaghetti squash', 'red bell pepper', 'yellow bell pepper', 'red onion', 'oregano', 'shrimp', 'olives'], 9, '/static/253.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (254, 'perfect  chocolate frosting', '86768', 15, '["15-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "north-american", "5-ingredients-or-less", "desserts", "american", "easy", "cakes", "chocolate", "dietary", "cake-fillings-and-frostings", "number-of-servings"]', ARRAY[4076.9, 491.0, 1181.0, 71.0, 53.0, 1001.0, 118.0], 14, ARRAY['place a large bowl , filled halfway with ice , in the sink', 'in a medium size pot over medium low heat , melt chocolate , cream , and butter', 'stirring constantly , cook the mixture until all of the butter and chocolate melts and thickens', 'cook for 5-6 minutes', 'do not let boil', 'remove pot from heat', 'whisk in the sifted confectioners sugar until mixture becomes smooth , then put the pot on top of the bowl of ice', 'beat with an electric mixer on low speed for 4-5 minutes until frosting becomes smooth , thick , and fudgy', 'remove from ice', 'spread onto cooled cake layers', 'the frosting will thicken as it sets up', 'if the frosting gets too hard to spread , put back on low heat and stir constantly until you get the spreading consistency desired', 'i have been adding 1 tbs', 'of light corn syrup to the initial heavy cream and chocolate mixture to add more glossiness to the frosting'], 'this is now one of my favorite chocolate frostings. i got it from the chocolate cake mix doctor by anne byrn. i have made a few changes to the recipe. actually, i didnt have the 1 cup of semisweet chocolate chips that the original recipe called for. lennie came to the rescue (thanks!) by figuring out the appropriate amount of semisweet squares to use, which i did have on hand. now i always make it with the squares instead of the chips. this frosting is very thick, creamy, and fudgy. i usually bake two 9-inch layers, cut them into four layers, and slather this frosting on each and every layer and all over the top and sides--because i like a little cake with my frosting :) another thanks goes out to leeannr for making me post this recipe. now she loves it as much as i do.', ARRAY['semisweet chocolate', 'heavy cream', 'butter', 'confectioners sugar'], 4, '/static/254.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (256, 'pizza pan  potato skins', '60938', 60, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "appetizers", "lunch", "side-dishes", "snacks", "eggs-dairy", "potatoes", "vegetables", "oven", "dinner-party", "finger-food", "cheese", "deep-fry", "stove-top", "dietary", "comfort-food", "taste-mood", "equipment", "presentation", "served-hot", "technique"]', ARRAY[503.9, 45.0, 9.0, 23.0, 37.0, 68.0, 13.0], 10, ARRAY['preheat oven to 375f', 'cook the diced bacon until crispy , remove from bacon fat and drain on paper towel', 'forget everything you were ever taught about peeling potatoes , and peel them with a knife leaving a good bit of potato on the skin', 'retain center of potatoes for another recipe , or slice them into wedges and cook in the same manner as the potato skins', 'deep fry the potato skins until crispy and golden', 'drain thoroughly and salt to taste', 'spread the potato skins evenly on a pizza pan', 'sprinkle evenly with the green onions , cheese and bacon', 'bake for 10-15 minutes until hot and cheddar is melted', 'serve hot with sour cream for dipping'], 'theres a pub we go to that makes their own french fries, and instead of throwing out the skins, they fry them up and bake them with toppings in a pizza pan. they bring them sizzling to your table in the pizza pan with sour cream for dipping. dare i say that they are better than the french fries? not a low calorie item but so good. this is my attempt to duplicate them.', ARRAY['potatoes', 'bacon', 'cheddar cheese', 'green onions', 'oil', 'salt', 'sour cream'], 7, '/static/256.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (259, 'puddingkuchen   custard bake', '353171', 75, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "desserts", "eggs-dairy", "fruit", "easy", "beginner-cook", "vegetarian", "dietary", "low-sodium", "comfort-food", "low-in-something", "brunch", "taste-mood", "sweet", "4-hours-or-less"]', ARRAY[1582.6, 88.0, 402.0, 27.0, 96.0, 156.0, 73.0], 6, ARRAY['in a bowl combine eggs , sugar , flour and milk', 'grease a stoneware dish with all the butter', 'pour in the dough and add fruit , raisins or almonds to taste', 'bake in the cold oven at 180c for about 60 minutes or until custard has set', 'you might need to cover the dish after 40 minutes or so', 'allow to stand and set for at least 10 minutes before serving'], 'this recipe comes from belgium. it is a lovely custard fruit bake, which resembles a cake. can be eaten warm or cold served with vanilla ice-cream or just plain.ive never used frozen berries in this. you can try but then make sure that they are not thawed or your custard wont set.', ARRAY['eggs', 'sugar', 'flour', 'milk', 'butter', 'fruit'], 6, '/static/259.jpg');
