INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (310, 'almost  boston market creamed spinach', '77206', 50, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "side-dishes", "vegetables", "dietary", "copycat", "low-calorie", "low-carb", "low-in-something", "greens", "spinach", "novelty"]', ARRAY[337.9, 43.0, 9.0, 24.0, 18.0, 84.0, 5.0], 14, ARRAY['in a saucepan melt butter over medium heat until sizzling', 'if you are adding in the fresh garlic and a pinch of cayenne pepper then saute in butter for about 2 minutes but do not brown the garlic', 'whisk in the flour and 1 / 2 tsp seasoned salt until creamed together and smooth', 'stir in the half and half , or milk a little at a time', 'increase heat to medium and constantly whisk until the mixture becomes thick and smooth', 'remove from heat', 'set aside', 'place 2 tbsp butter in saucepan over med heat', 'add the 2 tbsp minced onion , and cook until transparent', 'add spinach and water to pan , lower the heat , and cover', 'stirring several times until the spinach is cooked', 'when the spinach is almost done , add the prepared white sauce , sour cream and parmesan cheese', 'stir well , and simmer until completely blended', 'season with more seasoned salt or white salt if desired and black pepper'], 'this is quite similar to the creamed spinach at boston market, well so im told anyway! --- this is a great side dish, even people that hate spinach love this, its great to serve along side a holiday turkey or a ham dinner, or at any meal --- i also like to saute about 2 teaspoons fresh minced garlic and a pinch of cayenne pepper in the butter for about 2 minutes before adding in the flour but that is optional, add in some grated parmesan cheese too if desired, this complete recipe can be doubled :)', ARRAY['butter', 'flour', 'salt', 'half-and-half', 'sour cream', 'onions', 'water', 'frozen spinach', 'ground black pepper', 'parmesan cheese'], 10, '/static/310.jpg');
