INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (312, 'almost  starbucks frappuccino', '289671', 5, '["15-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "for-1-or-2", "low-protein", "beverages", "eggs-dairy", "easy", "refrigerator", "beginner-cook", "summer", "chocolate", "smoothies", "food-processor-blender", "dietary", "low-sodium", "low-cholesterol", "seasonal", "shakes", "low-calorie", "low-carb", "low-in-something", "equipment", "small-appliance", "number-of-servings"]', ARRAY[313.0, 19.0, 136.0, 8.0, 10.0, 35.0, 15.0], 6, ARRAY['in a blender combine coffee with ice cream and chocolate syrup', 'blend until smooth', 'add in sugar and blend slightly until combined', 'place about 1 cup crushed ice in two large tall glasses', 'divide the mixture into the glasses', 'top with a dollup of whipped cream'], 'since i serve this is very large glasses i have listed at two servings, you may divide into as many servings as you wish, make certain to use very strong coffee for this --- this is very good!', ARRAY['strong coffee', 'chocolate ice cream', 'chocolate syrup', 'sugar', 'ice', 'whipped cream'], 6, '/static/312.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (313, 'amerikaner   german vanilla biscuit', '356884', 25, '["30-minutes-or-less", "time-to-make", "course", "preparation", "occasion", "desserts", "easy", "beginner-cook", "kid-friendly", "picnic", "vegetarian", "dietary", "brunch", "to-go"]', ARRAY[233.0, 12.0, 96.0, 5.0, 6.0, 23.0, 12.0], 12, ARRAY['preheat the oven to 180c / 350f', 'in a big bowl cream butter , sugar and vanilla', 'little by little add the milk and the eggs', 'in a second bowl mix flour , baking powder and starch', 'add to the butter-mixture and stirr in until just blended', 'using 2 tablespoons drop 6 heaps of dough onto each paper-lined baking sheet', 'make sure to leave enough space in between the heaps as dough will spread during baking', 'bake for 15 minutes or until golden brown', 'transfer to a wire-rack and allow to cool', 'if using the glaze stir together icing sugar and lemon juice and spread this onto the even side of the amerikaner', 'idea: for children you can spread the curved side of the biscuit with a chocolate icing and stick slivered almonds into them to make them appear like little hedgehogs'], 'this famous sweet biscuit literally translates into american, referring to the gis baking this biscuit in germany after ww ii.it has a fluffy soft texture and a wonderfully vanilla taste. typically it is covered with a lemon icing (and i will give that option here), but i like it way better without. so do as you please. :)', ARRAY['butter', 'sugar', 'vanilla', 'eggs', 'milk', 'flour', 'starch', 'baking powder', 'icing sugar', 'lemon'], 10, '/static/313.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (315, 'amish  cake frosting   icing', '44050', 5, '["15-minutes-or-less", "time-to-make", "course", "cuisine", "preparation", "occasion", "north-american", "low-protein", "5-ingredients-or-less", "desserts", "american", "easy", "heirloom-historical", "holiday-event", "cakes", "dietary", "cake-fillings-and-frostings", "amish-mennonite", "northeastern-united-states", "low-in-something", "number-of-servings", "3-steps-or-less"]', ARRAY[1826.2, 178.0, 741.0, 45.0, 31.0, 382.0, 62.0], 2, ARRAY['beat cream cheese , sugar , vanilla& salt until fluffy', 'fold in cool whip'], 'yummmmmy! my amish mother-in-law shared this passed-down-the-generations recipe, and it is amazing! hard to beat - and goes well with almost any flavor of cake! enjoy!', ARRAY['cream cheese', 'brown sugar', 'vanilla', 'salt', 'cool whip'], 5, '/static/315.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (317, 'any  muffins', '242', 40, '["60-minutes-or-less", "time-to-make", "course", "preparation", "healthy", "breads", "oven", "muffins", "dietary", "quick-breads", "equipment"]', ARRAY[198.1, 9.0, 33.0, 7.0, 9.0, 16.0, 10.0], 44, ARRAY['bake 15 to 20 minutes in a 400 oven', 'for the muffins: grain: use 2 to 2 1 / 2 cup of white flour', 'or --', 'substitute oatmeal , cornmeal , whole wheat flour , rye flour , or flake cereal for 1 cup of the white flour', 'or --', 'substitute 1 cup leftover cooked oatmeal , rice or cornmeal for 1 / 2 cup of the white flour and decrease liquid to 1 / 2 cup', 'milk: use 1 cup', 'or --', 'substitute buttermilk or sour milk', 'or substitute fruit juice for part or all of the milk', 'fat: use 1 / 4 cup vegetable oil or 4 tbsp melted butter or margarine', 'or substitute regular or crunch peanut butter for part or all of the fat', 'the fat can be reduced or omitted with fair results if using a "wet addition', '" egg: use 1 egg', 'or --', 'substitute 1 heaping tbsp of soy flour and 1 tbsp of water', 'if using a cooked grain , separate the egg , add the yolk to the batter , beat white until stiff , and fold into the batter', 'sweetener: use between 2 tbsp and 1 / 2 cup sugar', 'or --', 'substitute up to 3 / 4 cup brown sugar', 'or -- substitute up to 1 / 2 cup honey or molasses and decrease milk to 3 / 4 cup', 'baking powder: use 2 teaspoons', 'if using whole or cooked grains or more than 1 cup additions , increase to 3 teaspoons', 'if using buttermilk or sour milk , decrease to 1 tsp and add 1 / 2 tsp baking soda', 'salt: use 1 / 2 tsp , or omit if you have a salt-restricted diet', 'optional ingredients: the following ingredients are optional', 'additions can be used in any combination , up to 1 1 / 2 cups total', 'if using more than 1 cup of wet additions , decrease the milk to 1 / 2 cup', 'dry additions: nuts , sunflower seeds , raisins , coconut , and so on', 'moist additons: blueberries , chopped apple , shredded zucchini , shredded carrot , and so on', 'wet additions: pumpkin puree , applesauce , mashed potato , mashed bananas , mashed carrot , and so on', 'if using 1 / 2 cup drained canned fruit or thawed shredded zucchini , substitute the syrup or zucchini liquid for all or part of the milk', 'spices: use spices that complement the additions , such as 1 tsp cinnamon with 1 / 4 tsp nutmeg or cloves', 'try 2 tsp grated orange or lemon peel', 'jellies and jam: fill cups half full with a plain batter', 'add 1 tsp jam or jelly and top with 2 more tbsp batter', 'nonsweet combinations: use only 2 tbsp sugar and no fruit', 'add combinations of the following: 1 / 2 cup shredded cheese , 2 strips fried and crumbled bacon , 2 tbsp grated onion , 1 / 2 cup shredded zucchini , 2 tbsp parmesan cheese', 'spices could include parsley , marjoram , rosemary --', 'alena kibblers additions: hawaiian muffins: use 1 / 2 cup mashed banana , 1 / 2 cup drained crushed pineapple , and 1 / 2 cup flake coconut , and 1 tbsp vanilla or rum flavoring', 'use the pineapple juice instead of milk', 'cherry vanilla chip muffins: 3 / 4 cup pitted chopped cherries , 3 / 4 cup vanilla chips , 1 tsp vanilla', 'elvis muffins: use about 3 / 4 to 1 cup mashed banana , 1 / 2 cup chopped peanuts or walnuts', 'and substitute 1 / 4 peanut butter for the oil'], 'muffins easy to adapt depending on your preferences and what you have available.', ARRAY['white flour', 'milk', 'fat', 'egg', 'sugar', 'baking powder', 'salt'], 7, '/static/317.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (318, 'apple pie', '124853', 43200, '["time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "beverages", "fruit", "american", "southern-united-states", "1-day-or-more", "easy", "dinner-party", "fall", "holiday-event", "cocktails", "dietary", "seasonal", "apples", "3-steps-or-less"]', ARRAY[285.8, 0.0, 281.0, 0.0, 0.0, 0.0, 24.0], 2, ARRAY['mix ingredence together except everclear and pour in to a ceramic jug that light will not penetrate close with cork or lid put jug in a cool dark place for 1 month', 'after 1 month shake up mixture and strain with cloth add everclear and serve cold'], 'a home made alcohalic drink it take 1 month for this drink to be perfect i add everclear for more of a kick but it is not needed but you will need a ceramic jug', ARRAY['apple juice', 'raw honey', 'whole cloves', 'everclear alcohol', 'ground cinnamon', 'cheesecloth'], 6, '/static/318.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (320, 'aww   shucks  crescent dough dessert', '161843', 50, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "for-large-groups", "breakfast", "desserts", "eggs-dairy", "fruit", "oven", "easy", "potluck", "dinner-party", "finger-food", "kid-friendly", "dietary", "inexpensive", "brunch", "taste-mood", "sweet", "to-go", "equipment", "number-of-servings", "presentation"]', ARRAY[308.5, 17.0, 123.0, 8.0, 7.0, 34.0, 15.0], 14, ARRAY['heat oven to 350', 'grease one 13x9 inch pan', 'roll out first can of crescent dough into bottom of pan , and slightly up sides', 'beat cream cheese , 1 cup of the sugar , and vanilla together', 'spread over crescent dough', 'microwave jam or preserves for 45 seconds , stirring once after twenty five seconds , to make it slightly liquid', 'spread jam on top of cream cheese , taking care not to mix the two together but keeping them layered', 'with your hands , gently stretch second can of crescent dough and place on top of jam layer', 'this does not have to cover perfectly , in fact , open seams here and there look quite pretty with the jam peeking through', 'mix melted butter , remaining 3 / 4 cup sugar , and cinnamon together , making sure to incorporate all the butter', 'gently spoon butter mixture over dough , spreading to cover', 'bake , uncovered , for 35 minutes , until top is browned nicely', 'let it cool for fifteen minutes , cut and serve !', 'tastes best warm or room temp , but cold is delicious , too'], 'this is a souped up version of another zaar recipe, that i played around with until i thought it couldnot get any better.  when i bring this to social gatherings, people are so estatic in their compliments, that i have to say "aw, shucks, its easy."  hence, the name.', ARRAY['crescent roll dough', 'cream cheese', 'sugar', 'vanilla', 'jam', 'butter', 'cinnamon'], 7, '/static/320.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (322, 'bailey s hot fudge sauce', '202009', 30, '["30-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "occasion", "for-1-or-2", "5-ingredients-or-less", "sauces", "desserts", "condiments-etc", "easy", "chocolate", "gifts", "sweet-sauces", "number-of-servings"]', ARRAY[1145.6, 105.0, 470.0, 21.0, 21.0, 214.0, 45.0], 6, ARRAY['over medium low heat melt butter and chocolate squares , take off heat', 'add 1 / 3 of the sugar and 1 / 3 of the evaporated milk , mix with a whisk until smooth', 'add remaining sugar and milk , stir until smooth', 'return to heat and simmer for 8 to 10 minutes', 'pour into canning jars or other container , let cool and close jar', 'recipe can be doubled'], 'this recipe is from "baileys" of boston. long gone ice cream parlor but had great hot fudge sauce!', ARRAY['butter', 'unsweetened chocolate squares', 'confectioners sugar', 'evaporated milk'], 4, '/static/322.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (323, 'baked  apple cinnamon oatmeal', '372665', 10, '["15-minutes-or-less", "time-to-make", "course", "main-ingredient", "preparation", "for-1-or-2", "granola-and-porridge", "breakfast", "easy", "grains", "inexpensive", "pasta-rice-and-grains", "number-of-servings"]', ARRAY[109.3, 5.0, 38.0, 1.0, 3.0, 3.0, 6.0], 8, ARRAY['core and peel apple', 'place in a microwaveable safe bowl and sprinkle brown sugar and cinnamon in apple', 'stuff it with cranberries and oats and top it with butter', 'cover with saran wrap and microwave for about 3 1 / 2 minutes', 'let it rest for about 2 minutes', 'chop the apple and add it to the dry instant oatmeal', 'follow the packet directions and add the juice from the cooked apple to the bowl', 'microwave about 1 1 / 2 minutes'], 'this is a twist on regular oatmeal for breakfast. its quick because you "bake" the apples in the microwave and use instanat oatmeal.note: most of y measurements are guestimates because this was the first time i made this recipe so feel free to add or subtract ingredients. this makes 2 servings but you can easy double it.', ARRAY['apple', 'brown sugar', 'cinnamon', 'oats', 'dried cranberries', 'margarine', 'oatmeal'], 7, '/static/323.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (326, 'beat the blues  blue cheese risotto', '137701', 40, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "main-dish", "eggs-dairy", "rice", "european", "winter", "italian", "cheese", "dietary", "one-dish-meal", "seasonal", "comfort-food", "pasta-rice-and-grains", "taste-mood", "savory", "presentation", "served-hot"]', ARRAY[1053.2, 79.0, 40.0, 95.0, 86.0, 118.0, 32.0], 18, ARRAY['nb: i like to use a fairly strong flavoured , but creamy blue cheese , like gorgonzola , but stilton is also nice', 'stock-wise , you may need a little more or less than in the ingredients list', 'bring stock up to a simmer', 'halve the leeks and remove any gritty bits from inside the rings', 'cut the leek into fine strips rather than semi-circles , of approx 1-2 inches in length', 'melt half the butter with the olive oil and cook the leeks for approx 10 minutes over a low-medium heat until soft and tinged with brown', 'add bacon to pan and cook for about 5 minutes', 'stir in garlic and rice , cook for a further minute or two , stirring the rice until its coated in the butter and oil and starts to go slightly translucent', 'add the wine to the pan , turning up the heat to bubble most of it away', 'add a ladleful of hot stock to the leek and rice mixture at a time , stirring more or less constantly', 'add more stock each time that most of the liquid has been absorbed', 'add the fresh sage', 'keep doing this for approximately 15 - 25 minutes , until the risotto is creamy , but the rice grains still have a slight bite', 'you will need to start testing the rice after about 15 minutes , but it may take a little longer', 'turn off the heat', 'stir in the lemon juice , zest , remaining butter , parmesan and blue cheese', 'leave the risotto for another 2 minutes until rich , creamy and luscious', 'season to taste with black pepper and salt if needed'], 'risotto is the ultimate comfort food, its like being hugged by your dinner. you can make do with a spoon and do not even need to bother with proper grown-up cutlery.a bowl of this can (almost) make everything better again when your down.', ARRAY['butter', 'olive oil', 'leeks', 'garlic cloves', 'smoked bacon', 'arborio rice', 'dry white wine', 'chicken stock', 'fresh sage', 'lemon, juice and zest of', 'blue cheese', 'parmesan cheese', 'fresh ground black pepper'], 13, '/static/326.jpg');
