INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (334, 'biggest loser  fish tacos', '392158', 50, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "north-american", "healthy", "lunch", "seafood", "mexican", "fish", "baja", "dietary", "low-cholesterol", "sandwiches", "healthy-2", "low-in-something", "equipment", "grilling"]', ARRAY[323.5, 13.0, 9.0, 32.0, 40.0, 12.0, 13.0], 12, ARRAY['to make the fish: place the fish in a shallow baking dish and sprinkle with the lime juice , paprika , salt , black pepper , and chill powder', 'cover , refrigerate , and marinate for about 30 minutes', 'preheat the grill to medium-high heat', 'lightly coat a 24 x 12 inch piece of foil with olive oil cooking spray', 'place the fish in a single layer in the center of the foil', 'fold the foil over the and fold the ends upward to seal in the fish', 'place the foil packet on the preheated grill', 'cook for 7 to 10 minutes , or until the fish is opaque', 'remove from the grill', 'to assemble the tacos: warp the tortillas in foil and place them on the grill to warm for 2 minutes', 'spread about one-eighth of the mashed avocado on each tortilla and top with one-eight of the the fish', 'sprinkle each with cheese , salsa , cilantro , cabbage , and hot sauce , if desired'], 'this is out of the "biggest loser" cookbook 140 calories, 18 g protein, 13 g carbohydrates, 5 g fat, 30 mg cholesterol, 8 g fiber, 480 mg sodium. please take the advice from sjmittens and increase the spices, especially if you enjoy spicy food.', ARRAY['fish fillets', 'lime juice', 'paprika', 'salt', 'ground black pepper', 'chili powder', 'tortillas', 'avocado', 'cheese', 'salsa', 'fresh cilantro', 'cabbage', 'hot sauce'], 13, '/static/334.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (335, 'blond  minestrone', '167554', 60, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "soups-stews", "vegetables", "european", "dinner-party", "italian", "food-processor-blender", "stove-top", "onions", "squash", "equipment", "small-appliance"]', ARRAY[191.8, 4.0, 22.0, 37.0, 23.0, 3.0, 10.0], 16, ARRAY['chop onions', 'heat oil in dutch oven over medium heat', 'add onion to pan and saute until softened - about 3-5 minutes', 'add oregano , basil and garlic , saute about 1 minute', 'stir in squash , zucchini , carrot and corn', 'saute until veggies are tender -- about 5 minutes', 'stir in broth bring to a boil , then reduce heat to low and simmer about 20 minutes', 'add pasta and beans to pan', 'cook about 10 minutes or until the pasta is tender', 'remove from heat', 'stir in spinach , add salt and pepper to taste', 'ladle into bowls', 'top with a dollop of pesto', 'garnish with freshly ground black pepper', 'if you must have tomatoes -- add 3 cups tomato to 2 of the 6 cups of broth , process in blender / food processor until smooth', 'add an additional cup of chopped tomatoes to the soup'], 'having recently discovered an allergy to tomatoes sent my italian world into a tailspin. how would i ever survive?? here is a wonderful, nourishing, comfort-food soup that i put together that eased my sense of deprivation. the amounts are very flexible, plus i have given directions on how to add tomatoes if you "must".', ARRAY['olive oil', 'onion', 'dried oregano', 'dried basil', 'garlic cloves', 'yellow squash', 'zucchini', 'carrot', 'fresh corn', 'chicken broth', 'ditalini', 'great northern beans', 'baby spinach', 'salt', 'fresh coarse ground black pepper', 'pesto sauce'], 16, '/static/335.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (336, 'boo tiful  jell o cups', '505132', 50, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "5-ingredients-or-less", "gelatin", "desserts", "snacks", "american", "easy", "refrigerator", "fall", "holiday-event", "kid-friendly", "vegan", "vegetarian", "chocolate", "stove-top", "dietary", "halloween", "seasonal", "midwestern", "taste-mood", "sweet", "equipment"]', ARRAY[150.0, 11.0, 73.0, 4.0, 4.0, 23.0, 6.0], 18, ARRAY['add boiling water to gelatin mix in large bowl', 'stir 2 minutes until completely dissolved', 'add ice', 'stir 3 to 5 minutes or until gelatin is thickened', 'remove any unmelted ice', 'whisk in 1 / 3 cup cool whip until blended', 'pour into 4 dessert dishes', 'refrigerate 30 minutes or until firm', 'drop remaining cool whip by spoonfuls onto desserts to resemble ghosts', 'add chocolate chips for the eyes', 'kraft kitchens', 'how to thaw cool whip whipped topping:', 'the best way to thaw frozen cool whip whipped topping is in its container in the refrigerator', 'depending on the size tub you havem the thawing time varies', 'recommended thawing times are: 8-ounce tube - 4 hours', '12-ounce tub - 5 hours', '16-ounce tub - 6 hours', 'we do not recommend thawing cool whip whipped topping in the microwave'], 'this was so good...everyone loved them.', ARRAY['boiling water', 'orange gelatin', 'ice cubes', 'cool whip topping', 'miniature semisweet chocolate chips'], 5, '/static/336.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (338, 'buffalo wing  mushrooms', '535020', 30, '["30-minutes-or-less", "time-to-make", "course", "preparation", "appetizers"]', ARRAY[2526.8, 345.0, 35.0, 106.0, 43.0, 87.0, 37.0], 8, ARRAY['in a medium-large mixing bowl , add in panko breadcrumbs and set aside', 'in a medium sized bowl , mix together flour , cornstarch , salt and black pepper', 'slowly whisk in water , making sure that it is not clumpy', 'in batches of three to four , drop in hen of the woods mushrooms into slurry then promptly into breadcrumbs', 'toss mushrooms in panko until the mushrooms are completely coated', 'remove the mushrooms from bowl and set out on a baking tray , making sure not to stack them up', 'bring four cups of canola oil up to 350f in a two-quart pot and drop in mushrooms', 'fry until golden brown and toss in hot sauce'], 'courtesy of ps kitchen.', ARRAY['mushrooms', 'cornstarch', 'all-purpose flour', 'salt', 'ground black pepper', 'seltzer water', 'panko breadcrumbs', 'canola oil'], 8, '/static/338.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (344, 'cheeseburger  cookies and  fries', '363009', 20, '["30-minutes-or-less", "time-to-make", "course", "preparation", "occasion", "for-large-groups", "desserts", "lunch", "snacks", "holiday-event", "kid-friendly", "cookies-and-brownies", "dietary", "number-of-servings"]', ARRAY[144.5, 10.0, 45.0, 3.0, 1.0, 11.0, 6.0], 15, ARRAY['place coconut into a ziploc sandwich bag and drop in several drops of green food coloring', 'roll around in your hands until all the coconut is tinted', 'set aside', 'in a separate bowl , tint some frosting red like ketchup and in another bowl , tint frosting yellow like mustard', 'set frosting aside', 'decide how many cookies you need , unwrap that many peppermint patties and set out twice as many wafter cookies', 'set half the wafer cookies , flat side up , onto a cookie sheet', 'put a small dab of vanilla frosting onto cookie and gently press down a peppermint patty', 'spread some "ketchup" and some "mustard" on top of the patty , sprinkle some "lettuce" on it and then place another cookie on top', 'brush a little honey on the top "bun" and sprinkle some sesame seeds onto them', 'set aside', 'take triangularly cut sheets of paper and fold them to resemble a french fry bag', 'if you want , use markers first to draw some golden arches and write "mc ___ on them', 'then , tape them together and fill with some potato sticks', 'place a cheeseburger and bag of fries on a small paper plate and serve !'], 'i made these every year for our 5 kids to take to school on their birthdays.  other kids and teachers would swarm to see them !  our kids are now teenagers and their old school friends still talk about how "cool" they are !', ARRAY['vanilla wafer cookies', 'shredded coconut', 'peppermint patties', 'sesame seeds', 'vanilla frosting', 'honey', 'green food coloring', 'red food coloring', 'yellow food coloring', 'potato sticks'], 10, '/static/344.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (345, 'cheeseburger stuffed  french bread', '113150', 40, '["60-minutes-or-less", "time-to-make", "course", "preparation", "lunch", "dietary", "sandwiches"]', ARRAY[790.6, 47.0, 56.0, 81.0, 94.0, 77.0, 27.0], 10, ARRAY['cook hamburger and onion , drain grease', 'add soup and spices , simmer 5-10 minutes', 'meanwhile , slice top third off bread , hollow out bottom half of loaf , leaving a 3 / 4-inch shell , save bread chunks , or feed to the birds !', 'spread top slice of bread , with butter', 'place hollowed out bread on cookie sheet and broil till lightly browned', 'take out of oven and fill with meat mixture , top with cheese slices', 'place buttered top next to it , put back in oven at 350', 'bake till heated through and top is toasted a little', 'put the top on , slice into wide slices for sandwiches , about 4-5 inches wide', '4-6 servings'], 'even though this is an easy one--its actually really good!', ARRAY['hamburger', 'onion', 'tomato soup', 'garlic salt', 'salt', 'pepper', 'french bread', 'butter', 'velveeta cheese'], 9, '/static/345.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (349, 'chinese  popcorn', '142984', 65, '["time-to-make", "course", "preparation", "lunch", "snacks", "easy", "4-hours-or-less"]', ARRAY[531.1, 63.0, 7.0, 17.0, 27.0, 51.0, 10.0], 9, ARRAY['preheat oven to 250 degrees f', 'in a large shallow roasting pan , combine popcorn , chow mein noodles , and peanuts', 'melt butter', 'add sesame oil , soy sauce , lemon juice , and ginger', 'pour over popcorn mixture', 'toss to coat evenly', 'bake for 1 hour , stirring every 20 minutes', 'spread on paper towels to cool', 'store in airtight container'], 'this looks like a tasty treat, with a nice asian flair.', ARRAY['popped corn', 'chow mein noodles', 'peanuts', 'butter', 'sesame oil', 'soy sauce', 'lemon juice', 'ground ginger'], 8, '/static/349.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (342, 'chmeur s  pudding', '357497', 62, '["time-to-make", "course", "cuisine", "preparation", "north-american", "low-protein", "healthy", "desserts", "canadian", "easy", "beginner-cook", "kid-friendly", "dietary", "low-sodium", "low-cholesterol", "inexpensive", "quebec", "low-in-something", "4-hours-or-less"]', ARRAY[340.7, 13.0, 207.0, 6.0, 4.0, 9.0, 21.0], 9, ARRAY['preheat oven to 325f', 'mix flour and baking powder , set aside', 'in a bowl , beat eggs , sugar , and margarine', 'incorporate flour , alternating with milk', 'without spreading it , pour preperation into a greased 13"x9" pan', 'mix all the ingredients for the sauce in a saucepan', 'boil for 2 minutes', 'pour over top of the dough', 'bake 45 minutes'], 'a classic french-canadian sweet. "chmeur" means un-employed; therefore, the dish bears its name because it is inexpensive. along with rice pudding and jello, this is one of most commonly offered desserts in diners. warning: may cause sugar overdose. contrary to the usual recipe, this one uses milk for a more cake-like texture. the original recipe also does not call for an egg, but my mother always made it with the egg.', ARRAY['flour', 'baking powder', 'margarine', 'sugar', 'milk', 'egg', 'vanilla', 'brown sugar', 'boiling water'], 9, '/static/342.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (280, 'splenda   d  cheesecake sugar free low carb', '185799', 80, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "desserts", "eggs-dairy", "easy", "beginner-cook", "diabetic", "romantic", "cheesecake", "cheese", "eggs", "dietary", "comfort-food", "taste-mood", "sweet", "4-hours-or-less"]', ARRAY[566.5, 80.0, 16.0, 14.0, 21.0, 150.0, 4.0], 27, ARRAY['grahm cracker crumb crust:', 'mix together: crumbs , splenda , spices , and almonds', 'stir in butter , mix well', 'press into bottom and slightly up sides of 10 inch spring form pan', 'set aside', 'filling:', 'first beat heavy whipping cream until it starts to look frothy , then add cup splenda and 1 teaspoons vanilla extract', 'continue beating until firm peaks form', 'set aside', 'filling:', 'preheat oven to 350f', 'in a separate bowl , thoroughly beat remaining filling ingredients with a mixer , for about 5 minutes', 'scraping down the sides occasionally', 'take half of whipped cream mixture and fold into filling', 'set aside other half of whipped cream mixture', 'pour filling into prepared pan', 'bake at 350f for 35 - 40 minute', 'remove from oven and let cool for 5- 10 minute', 'reset oven to 475f', 'while cheesecake is cooling prepare topping', 'topping:', 'mix together topping ingredients and pour over cake', 'return cheesecake to oven and bake at 475f for 8 - 12 minute', 'cool , and chill cake for at least 4 hours before serving', 'just before serving decorate with remaining whipped cream and fruit of your choice', 'i prefer blackberries', 'enjoy !'], 'this is my own recipe for a yummy, creamy, thick, cheesecake; that is easy to make and sure to  dazzle anyone.i prefer to serve it topped with blackberries, but you can use any fruit of your choice i bet kiwi with lime would be good too, mmmm mabe i will go make another.to be completely honest, i opt not to dig out my spring form pan and cheat by buying ready made crust from the store. although, i have included ingredients and instructions for a crust for those who are looking for a completely home made cake. however, if you are as lazy as i am and go with the ready made crust  tip: keep that aluminum edge (that keeps the clear plastic lid on) folded up, making an extra lip. it will help hold more cheesecake, and keep it from overflowing. this is a nice, thick cheesecake! this cheesecake is made in three parts 1. crust, 2. filling (note: filling is made in two stages whipped cream and filling), and 3. topping (note: this topping is not the fruit topping, but the top layer of the cheesecake).', ARRAY['graham cracker crumbs', 'splenda granular', 'cinnamon', 'nutmeg', 'slivered almonds', 'butter', 'heavy whipping cream', 'vanilla extract', 'cream cheese', 'eggs', 'cornstarch', 'lemon juice', 'sour cream'], 13, '/static/280.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (276, 'skordy  new potatoes w rosemary lemon   olive oi', '296983', 35, '["60-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "for-1-or-2", "appetizers", "salads", "potatoes", "vegetables", "greek", "easy", "european", "vegan", "vegetarian", "dips", "dietary", "number-of-servings"]', ARRAY[209.7, 10.0, 7.0, 0.0, 8.0, 4.0, 11.0], 14, ARRAY['preheat oven to gas mark 8', 'cut your potatoes into 1 inch chunks', 'toss in a bowl the potatoes in the oil with the rosemary and lemon juice and lemon zest and garlic', 'chuck the lot onto a baking tray not forgetting to scrape herbs and oil from the bowl', 'bake for twenty minutes while listening to funky tunes and enjoying the yumminess that is to come', 'check that potatoes are firm yet "give" with a fork', 'squish them a little bit with a fork and put back in oven for another 10 minutes on gas mark 3', 'listen to more tunes', 'serve with a squirt of fresh lemon and with greek or curry or vegan stuff or just eat out of pan', 'i suggest tatskiki', 'you can pulverize this into a dip using more olive oil and lemon juice or eat it chunky', 'i like chunks', 'i reckon this would make an ace potato salad base', 'i absolutely hate mayonnaise , so theres no chance of me making that ever ! however you are welcome to try--i will let you'], 'i took this recipe from a vegan tastes of greece for a  recipe called skordalia which seemed like a dip.  i wanted this flavour without dip, so i changed it a bit; hence skordy.  i listened to penulum while making greek treats which was rather funky do try!', ARRAY['new potatoes', 'lemon, juice of', 'lemon, zest of', 'rosemary', 'olive oil', 'garlic cloves'], 6, '/static/276.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (248, 'not  lasagna', '251528', 80, '["time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "main-dish", "beef", "pasta", "easy", "european", "kid-friendly", "italian", "dietary", "comfort-food", "inexpensive", "ground-beef", "meat", "pasta-rice-and-grains", "ravioli-tortellini", "taste-mood", "savory", "4-hours-or-less"]', ARRAY[572.9, 48.0, 76.0, 60.0, 89.0, 69.0, 8.0], 12, ARRAY['preheat oven to 350f', 'spray an 8-inch glass baking dish with non-stick spray', 'measure 1 / 2 cup spaghetti sauce from jar and spread evenly on bottom of dish', 'brown ground beef in a skillet over medium heat', 'drain and return to skillet', 'add remaining spaghetti sauce , italian seasoning and garlic salt', 'simmer for 5 minutes', 'layer half of ravioli on the sauce in the dish', 'spread half of the beef mixture over the ravioli and top with 1 cup of the mozzarella cheese', 'repeat layers one more time , ending with cheese', 'bake for 1 hour', 'allow to stand for 5 minutes before serving'], 'this recipe, from karin calloway of the augusta chronicle, eliminates many of the steps in creating traditional lasagna.  instead of the cheese mixture and noodles, you use cheese ravioli.  you layer the ravioli with meat sauce and mozzarella cheese and bake.  voila! --  its "not" lasagna;and it is delicious and perfect for a weeknight supper.', ARRAY['lean ground beef', 'spaghetti sauce', 'italian seasoning', 'garlic salt', 'cheese ravioli', 'mozzarella cheese'], 6, '/static/248.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (238, 'meal in a bowl   guacamole salad', '140855', 25, '["30-minutes-or-less", "time-to-make", "course", "main-ingredient", "cuisine", "preparation", "occasion", "north-american", "salads", "fruit", "seafood", "mexican", "easy", "fish", "comfort-food", "tuna", "saltwater-fish", "taste-mood", "presentation", "served-cold", "3-steps-or-less"]', ARRAY[474.1, 57.0, 12.0, 18.0, 52.0, 54.0, 3.0], 6, ARRAY['to make the salad: have all ingredients chilled', 'break the lettuce into a serving bowl', 'add other ingredients except cheese', 'toss lightly with avocado dressing', 'top with grated cheese', 'to make the avocado dressing: put all ingredients in the blender and blend well'], 'this is a great alternative to the traditional taco salad.  it truly is a whole meal salad;just add hot, crusty rolls and maybe some white wine and a light desssert for a great luncheon or supper.  it goes together quickly, just be sure to have the ingredients chilled.   this recipe was given to me by a hispanic teacher at our school so i tend to think of it as "authentic".', ARRAY['lettuce', 'tomatoes', 'pitted ripe olives', 'green onion', 'corn chips', 'tuna', 'cheddar cheese', 'avocado', 'lemon juice', 'sour cream', 'salad oil', 'garlic clove', 'sugar', 'chili powder', 'salt', 'tabasco sauce'], 16, '/static/238.jpg');
INSERT INTO recipes_SampleRecipe (snum, name, recipe_id, minutes, tags, nutrition, n_steps, steps, description, ingredients, n_ingredients, image_url) VALUES (224, 'kicked up  oven baked pork baby back ribs', '171442', 80, '["time-to-make", "course", "main-ingredient", "preparation", "occasion", "main-dish", "pork", "oven", "easy", "beginner-cook", "dinner-party", "holiday-event", "dietary", "spicy", "comfort-food", "meat", "pork-ribs", "taste-mood", "equipment", "4-hours-or-less"]', ARRAY[1398.0, 165.0, 83.0, 56.0, 149.0, 198.0, 9.0], 10, ARRAY['set oven to 425f', 'line a baking sheet with foil , then arrange the ribs in a single layer', 'season with season salt and pepper', 'bake for about 25 minutes', 'turn the ribs over and bake for another 15 minutes or until browned', 'meanwhile in a bowl combine ketchup , hoisin , lemon juice , garlic , louisiana hot sauce , habanero chili hot sauce , cinnamon , allspice and seasoned salt', 'mix well to combine', 'baste the ribs with about half the sauce', 'cook for another 10 minutes more', 'turn over and baste with the remaining sauce and continue to bake for another 15 minutes or until the ribs are thoroughly cooked through'], 'if you like heat then you will love these ribs! for level 1 heat; use 1 tablespoon habanero hot sauce...for level 2; use 2 tablespoons... and for level 3; use 3 tablespoons. habanero chili hot sauce can be found in any mexican section of your grocery store, since my family loves extreme heat i use it in many recipes. this recipe can be doubled.', ARRAY['pork baby back ribs', 'seasoning salt', 'pepper', 'ketchup', 'hoisin sauce', 'lemon juice', 'sugar', 'fresh garlic', 'louisiana hot sauce', 'habanero sauce', 'cinnamon', 'allspice'], 12, '/static/224.jpg');
